import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, collection, query, where, onSnapshot, serverTimestamp, addDoc, updateDoc, getDocs } from 'firebase/firestore';
import { useForm } from 'react-hook-form';

// The following global variables are provided by the Canvas environment.
// We must always use them for Firebase setup.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = {
    apiKey: "AIzaSyBh7ErxnKurtNkeK_0PW5XW4joDe_r9Ut4",
    authDomain: "farmers-management-syste-84643.firebaseapp.com",
    projectId: "farmers-management-syste-84643",
    storageBucket: "farmers-management-syste-84643.firebasestorage.app",
    messagingSenderId: "410779424956",
    appId: "1:410779424956:web:b1d26dab35719b1e9a18b9",
    measurementId: "G-RD879ML6B6"
};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

// ----------------------
// Firebase Initialization
// ----------------------
// Initialize Firebase app and services
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// ----------------------
// Main App Component
// ----------------------
export default function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [loading, setLoading] = useState(true);
    const [requests, setRequests] = useState([]);
    const [locations, setLocations] = useState({ regions: [], zones: [], woreda: [] });
    const [departments, setDepartments] = useState([]);
    const [currentPage, setCurrentPage] = useState('dashboard');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);
    const [userRole, setUserRole] = useState(null);

    // Auth state listener
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                setCurrentUser(user);
                fetchUserRole(user.uid);
            } else {
                setCurrentUser(null);
                setUserRole(null);
            }
            setAuthReady(true);
            setLoading(false);
        });
        return () => unsubscribe();
    }, []);

    // Sign in with custom token or anonymously
    useEffect(() => {
        const signInUser = async() => {
            try {
                if (initialAuthToken) {
                    await signInWithCustomToken(auth, initialAuthToken);
                } else {
                    await signInAnonymously(auth);
                }
            } catch (error) {
                console.error("Error signing in:", error);
            }
        };

        if (!authReady) {
            signInUser();
        }
    }, [authReady]);

    // Firestore data listeners
    useEffect(() => {
        if (!authReady || !currentUser) return;

        // Listen for requests
        const requestsQuery = query(collection(db, `/artifacts/${appId}/public/data/requests`));
        const unsubscribeRequests = onSnapshot(requestsQuery, (snapshot) => {
            const fetchedRequests = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setRequests(fetchedRequests);
        }, (error) => {
            console.error("Error fetching requests:", error);
        });

        // Listen for locations
        const locationsQuery = query(collection(db, `/artifacts/${appId}/public/data/locations`));
        const unsubscribeLocations = onSnapshot(locationsQuery, (snapshot) => {
            const fetchedLocations = { regions: [], zones: [], woreda: [] };
            snapshot.docs.forEach(doc => {
                const data = doc.data();
                if (data.type === 'region') fetchedLocations.regions.push(data.name);
                if (data.type === 'zone') fetchedLocations.zones.push(data.name);
                if (data.type === 'woreda') fetchedLocations.woreda.push(data.name);
            });
            setLocations(fetchedLocations);
        }, (error) => {
            console.error("Error fetching locations:", error);
        });

        // Listen for departments
        const departmentsQuery = query(collection(db, `/artifacts/${appId}/public/data/departments`));
        const unsubscribeDepartments = onSnapshot(departmentsQuery, (snapshot) => {
            const fetchedDepartments = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setDepartments(fetchedDepartments);
        }, (error) => {
            console.error("Error fetching departments:", error);
        });

        return () => {
            unsubscribeRequests();
            unsubscribeLocations();
            unsubscribeDepartments();
        };
    }, [authReady, currentUser]);

    const fetchUserRole = async(uid) => {
        try {
            const userDocRef = doc(db, `/artifacts/${appId}/users/${uid}/userData/profile`);
            const userDoc = await getDoc(userDocRef);
            if (userDoc.exists()) {
                const role = userDoc.data().role;
                setUserRole(role);
            } else {
                setUserRole('farmer'); // Default role if no profile exists
                await setDoc(userDocRef, { role: 'farmer', createdAt: serverTimestamp() });
            }
        } catch (error) {
            console.error("Error fetching user role:", error);
        }
    };

    const handleNewRequest = async(data) => {
        try {
            await addDoc(collection(db, `/artifacts/${appId}/public/data/requests`), {
                ...data,
                status: 'Pending',
                createdAt: serverTimestamp(),
                userId: currentUser.uid
            });
            setModalContent('Request submitted successfully!');
            setShowModal(true);
            setCurrentPage('dashboard');
        } catch (error) {
            console.error("Error adding request:", error);
            setModalContent('Error submitting request.');
            setShowModal(true);
        }
    };

    const handleUpdateStatus = async(requestId, newStatus) => {
        try {
            const requestRef = doc(db, `/artifacts/${appId}/public/data/requests`, requestId);
            await updateDoc(requestRef, { status: newStatus, approvedBy: currentUser.uid, approvedAt: serverTimestamp() });
            setModalContent(`Request status updated to ${newStatus}.`);
            setShowModal(true);
            setCurrentPage('dashboard');
        } catch (error) {
            console.error("Error updating status:", error);
            setModalContent('Error updating request status.');
            setShowModal(true);
        }
    };

    const navigateToViewRequest = (request) => {
