import React from 'react';

function FederalDashboard({ currentUser, requests, onApprove }) {
    return ( <
        div >
        <
        h2 > Federal Dashboard - Welcome { currentUser.username } < /h2> <
        h3 > Requests < /h3> <
        ul > {
            requests.map(req => ( <
                    li key = { req.id } > { req.productName || 'Unknown' } - Status: { req.status || 'Pending' } {
                        req.status === 'Pending' && < button onClick = {
                                () => onApprove(req.id) } > Approve < /button>} <
                            /li>
                    ))
            } <
            /ul> <
            /div>
        );
    }

    export default FederalDashboard;
