import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MasterData() {
    const [admins, setAdmins] = useState([]);
    const [products, setProducts] = useState([]);
    const [modalType, setModalType] = useState(null); // 'addAdmin', 'editAdmin', 'addProduct', 'editProduct'
    const [formData, setFormData] = useState({});
    const token = localStorage.getItem('token');

    useEffect(() => {
        fetchAdmins();
        fetchProducts();
    }, []);

    const fetchAdmins = async() => {
        const res = await axios.get('http://localhost:5000/api/admin/list', {
            headers: { Authorization: `Bearer ${token}` }
        });
        setAdmins(res.data);
    };

    const fetchProducts = async() => {
        const res = await axios.get('http://localhost:5000/api/admin/products', {
            headers: { Authorization: `Bearer ${token}` }
        });
        setProducts(res.data);
    };

    const deleteAdmin = async(id) => {
        if (window.confirm("Are you sure you want to delete this admin?")) {
            await axios.delete(`http://localhost:5000/api/admin/delete${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchAdmins();
        }
    };

    const deleteProduct = async(id) => {
        if (window.confirm("Are you sure you want to delete this product?")) {
            await axios.delete(`http://localhost:5000/api/admin/products/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            fetchProducts();
        }
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            if (modalType === 'addAdmin') {
                await axios.post('http://localhost:5000/api/admin/register', formData, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                fetchAdmins();
            } else if (modalType === 'editAdmin') {
                await axios.put(`http://localhost:5000/api/admin/edit/${formData.id}`, formData, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                fetchAdmins();
            } else if (modalType === 'addProduct') {
                await axios.post('http://localhost:5000/api/admin/products', formData, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                fetchProducts();
            } else if (modalType === 'editProduct') {
                await axios.put(`http://localhost:5000/api/admin/products/${formData.id}`, formData, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                fetchProducts();
            }
            closeModal();
        } catch (err) {
            console.error(err);
        }
    };

    const openModal = (type, data = {}) => {
        setModalType(type);
        setFormData(data);
    };

    const closeModal = () => {
        setModalType(null);
        setFormData({});
    };

    return ( <
        div className = "space-y-8" > { /* Regional Admins */ } <
        div >
        <
        div className = "flex justify-between items-center mb-4" >
        <
        h2 className = "text-lg font-bold" > Regional Admins < /h2> <
        button onClick = {
            () => openModal('addAdmin') }
        className = "bg-green-500 text-white px-3 py-1 rounded" >
        +Add Admin <
        /button> <
        /div> <
        table className = "w-full bg-white rounded shadow" >
        <
        thead >
        <
        tr className = "bg-gray-200" >
        <
        th className = "p-2 text-left" > Name < /th> <
        th className = "p-2 text-left" > Phone < /th> <
        th className = "p-2" > Actions < /th> <
        /tr> <
        /thead> <
        tbody > {
            admins.map(admin => ( <
                tr key = { admin.id }
                className = "border-b" >
                <
                td className = "p-2" > { admin.name } < /td> <
                td className = "p-2" > { admin.phone } < /td> <
                td className = "p-2 text-center" >
                <
                button onClick = {
                    () => openModal('editAdmin', admin) }
                className = "bg-blue-500 text-white px-2 py-1 rounded mr-2" > Edit < /button> <
                button onClick = {
                    () => deleteAdmin(admin.id) }
                className = "bg-red-500 text-white px-2 py-1 rounded" > Delete < /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div>

        { /* Products */ } <
        div >
        <
        div className = "flex justify-between items-center mb-4" >
        <
        h2 className = "text-lg font-bold" > Products < /h2> <
        button onClick = {
            () => openModal('addProduct') }
        className = "bg-green-500 text-white px-3 py-1 rounded" >
        +Add Product <
        /button> <
        /div> <
        table className = "w-full bg-white rounded shadow" >
        <
        thead >
        <
        tr className = "bg-gray-200" >
        <
        th className = "p-2 text-left" > Name < /th> <
        th className = "p-2 text-left" > Category < /th> <
        th className = "p-2 text-left" > Description < /th> <
        th className = "p-2 text-left" > Amount < /th> <
        th className = "p-2" > Actions < /th> <
        /tr> <
        /thead> <
        tbody > {
            products.map(product => ( <
                tr key = { product.id }
                className = "border-b" >
                <
                td className = "p-2" > { product.name } < /td> <
                td className = "p-2" > { product.category } < /td> <
                td className = "p-2" > { product.description } < /td> <
                td className = "p-2" > { product.amount } < /td> <
                td className = "p-2 text-center" >
                <
                button onClick = {
                    () => openModal('editProduct', product) }
                className = "bg-blue-500 text-white px-2 py-1 rounded mr-2" > Edit < /button> <
                button onClick = {
                    () => deleteProduct(product.id) }
                className = "bg-red-500 text-white px-2 py-1 rounded" > Delete < /button> <
                /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> <
        /div>

        { /* Modal */ } {
            modalType && ( <
                div className = "fixed inset-0 flex items-center justify-center bg-black bg-opacity-40" >
                <
                div className = "bg-white rounded p-6 w-96 shadow" >
                <
                h3 className = "text-lg font-bold mb-4" > {
                    modalType.includes('Admin') ? (modalType.startsWith('add') ? 'Add Admin' : 'Edit Admin') : modalType.includes('Product') ? (modalType.startsWith('add') ? 'Add Product' : 'Edit Product') : ''
                } <
                /h3> <
                form onSubmit = { handleSubmit }
                className = "space-y-3" > {
                    modalType.includes('Admin') && ( <
                        >
                        <
                        input type = "text"
                        placeholder = "Name"
                        value = { formData.name || '' }
                        onChange = { e => setFormData({...formData, name: e.target.value }) }
                        className = "border p-2 w-full"
                        required / >
                        <
                        input type = "text"
                        placeholder = "Phone"
                        value = { formData.phone || '' }
                        onChange = { e => setFormData({...formData, phone: e.target.value }) }
                        className = "border p-2 w-full"
                        required / > {
                            modalType === 'addAdmin' && ( <
                                input type = "password"
                                placeholder = "Password"
                                value = { formData.password || '' }
                                onChange = { e => setFormData({...formData, password: e.target.value }) }
                                className = "border p-2 w-full"
                                required / >
                            )
                        } <
                        />
                    )
                } {
                    modalType.includes('Product') && ( <
                        >
                        <
                        input type = "text"
                        placeholder = "Name"
                        value = { formData.name || '' }
                        onChange = { e => setFormData({...formData, name: e.target.value }) }
                        className = "border p-2 w-full"
                        required / >
                        <
                        input type = "text"
                        placeholder = "Category"
                        value = { formData.category || '' }
                        onChange = { e => setFormData({...formData, category: e.target.value }) }
                        className = "border p-2 w-full"
                        required / >
                        <
                        textarea placeholder = "Description"
                        value = { formData.description || '' }
                        onChange = { e => setFormData({...formData, description: e.target.value }) }
                        className = "border p-2 w-full"
                        required / >
                        <
                        input type = "number"
                        placeholder = "Amount"
                        value = { formData.amount || '' }
                        onChange = { e => setFormData({...formData, amount: e.target.value }) }
                        className = "border p-2 w-full"
                        required / >
                        <
                        />
                    )
                } <
                div className = "flex justify-end space-x-2 mt-4" >
                <
                button type = "button"
                onClick = { closeModal }
                className = "bg-gray-400 text-white px-3 py-1 rounded" > Cancel < /button> <
                button type = "submit"
                className = "bg-green-500 text-white px-3 py-1 rounded" > Save < /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        } <
        /div>
    );
}
