import React, { useState } from 'react';
import MasterData from '../components/MasterData';
import RequestsTable from '../components/RequestsTable';
import ApprovalsTable from '../components/ApprovalsTable';
import ReportsView from '../components/ReportsView';

export default function FederalDashboard() {
    const [activeMenu, setActiveMenu] = useState('requests');

    return ( <
        div className = "flex h-screen" > { /* Sidebar */ } <
        div className = "w-64 bg-gray-800 text-white p-4" >
        <
        h2 className = "font-bold text-xl mb-6" > Dashboards < /h2> <
        ul className = "space-y-2" >
        <
        li >
        <
        button onClick = {
            () => setActiveMenu('requests') }
        className = { `w-full text-left hover:bg-gray-700 p-2 rounded ${
                                activeMenu === 'requests' ? 'bg-gray-700' : ''
                            }` } >
        ðŸ“¦Product Requests <
        /button> <
        /li> <
        li >
        <
        button onClick = {
            () => setActiveMenu('approvals') }
        className = { `w-full text-left hover:bg-gray-700 p-2 rounded ${
                                activeMenu === 'approvals' ? 'bg-gray-700' : ''
                            }` } >
        âœ…Approvals <
        /button> <
        /li> <
        li >
        <
        button onClick = {
            () => setActiveMenu('master') }
        className = { `w-full text-left hover:bg-gray-700 p-2 rounded ${
                                activeMenu === 'master' ? 'bg-gray-700' : ''
                            }` } >
        ðŸ› Master Data <
        /button> <
        /li> <
        li >
        <
        button onClick = {
            () => setActiveMenu('reports') }
        className = { `w-full text-left hover:bg-gray-700 p-2 rounded ${
                                activeMenu === 'reports' ? 'bg-gray-700' : ''
                            }` } >
        ðŸ“ŠReports <
        /button> <
        /li> <
        /ul> <
        /div>

        { /* Main Content */ } <
        div className = "flex-1 p-6 bg-gray-100 overflow-auto" > { activeMenu === 'requests' && < RequestsTable / > } { activeMenu === 'approvals' && < ApprovalsTable / > } { activeMenu === 'master' && < MasterData / > } { activeMenu === 'reports' && < ReportsView / > } <
        /div> <
        /div>
    );
}
