import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';

// ----------------------
// Mock Data (Simulates a database)
// ----------------------
// NOTE: This is your existing mock data. It is recommended to replace this with a real
// database solution like MySQL or Firebase in the future for persistent storage.
// This data now includes all administrative levels and users as requested.
const initialUsers = [{
    phoneNumber: '111-222-3333',
    password: 'password',
    role: 'Federal',
    fullName: 'Federal Admin',
    id: 'fed-1'
}, {
    phoneNumber: '444-555-6666',
    password: 'password',
    role: 'Region',
    fullName: 'Amhara Regional Admin',
    regionName: 'Amhara',
    id: 'reg-1'
}, {
    phoneNumber: '777-888-9999',
    password: 'password',
    role: 'Zone',
    fullName: 'North Shewa Zone Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    id: 'zone-1'
}, {
    phoneNumber: '987-654-3210',
    password: 'password',
    role: 'Woreda',
    fullName: 'Kewet Woreda Admin',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    id: 'wor-1'
}, {
    phoneNumber: '987-654-3211',
    password: 'password',
    role: 'Kebele',
    fullName: 'Alem Ketema Kebele Rep',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'keb-1'
}, {
    phoneNumber: '123-456-7890',
    password: 'password',
    role: 'Farmer',
    fullName: 'Abebe Kebede',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'farm-1'
}, {
    phoneNumber: '333-444-5555',
    password: 'password',
    role: 'Farmer',
    fullName: 'Chala Debele',
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    id: 'farm-2'
}, ];

// Each request now has a 'previousApprovals' object to track the status at each admin level
const initialRequests = [{
    id: 'REQ12345',
    requesterName: 'Abebe Kebede',
    requesterId: 'farm-1',
    productName: 'Tractor',
    productPurpose: 'Land preparation',
    amount: 1,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    status: 'Pending',
    previousApprovals: {
        Federal: 'Pending',
        Region: 'Pending',
        Zone: 'Pending',
        Woreda: 'Pending',
        Kebele: 'Pending',
    }
}, {
    id: 'REQ12346',
    requesterName: 'Chala Debele',
    requesterId: 'farm-2',
    productName: 'Fertilizer A',
    productPurpose: 'Improving soil quality',
    amount: 5,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    status: 'Approved',
    previousApprovals: {
        Federal: 'Pending',
        Region: 'Approved',
        Zone: 'Approved',
        Woreda: 'Accepted',
        Kebele: 'Rejected',
    }
}, {
    id: 'REQ12347',
    requesterName: 'Abebe Kebede',
    requesterId: 'farm-1',
    productName: 'Pesticides',
    productPurpose: 'Pest control',
    amount: 20,
    regionName: 'Amhara',
    zoneName: 'North Shewa',
    woredaName: 'Kewet',
    kebeleName: 'Alem Ketema',
    status: 'Rejected',
    previousApprovals: {
        Federal: 'Pending',
        Region: 'Pending',
        Zone: 'Rejected',
        Woreda: 'Pending',
        Kebele: 'Pending',
    }
}, ];

const App = () => {
        const [currentUser, setCurrentUser] = useState(null);
        const [allUsers, setAllUsers] = useState(initialUsers);
        const [requests, setRequests] = useState(initialRequests);
        const [view, setView] = useState('login'); // 'login', 'dashboard', 'registerUser'
        const [filterStatus, setFilterStatus] = useState('All');
        const [searchQuery, setSearchQuery] = useState('');
        const [showModal, setShowModal] = useState(false);
        const [modalContent, setModalContent] = useState({});

        // A map to determine which role can register the next lowest level
        const registrationRoles = {
            Federal: 'Region',
            Region: 'Zone',
            Zone: 'Woreda',
            Woreda: 'Kebele',
            Kebele: 'Farmer',
        };

        const handleLogin = (data) => {
            const user = allUsers.find(
                u => u.phoneNumber === data.phoneNumber && u.password === data.password
            );
            if (user) {
                setCurrentUser(user);
                setView('dashboard');
            } else {
                setModalContent({ title: "Login Failed", message: "Invalid credentials. Please try again." });
                setShowModal(true);
            }
        };

        const handleLogout = () => {
            setCurrentUser(null);
            setView('login');
        };

        const handleApproveReject = (requestId, newStatus) => {
            setRequests(requests.map(req => {
                if (req.id === requestId) {
                    // Update the status at the current user's level
                    const newPreviousApprovals = {...req.previousApprovals,
                        [currentUser.role]: newStatus
                    };
                    return {...req,
                        status: newStatus,
                        previousApprovals: newPreviousApprovals
                    };
                }
                return req;
            }));
        };

        const handleDeleteRequest = (requestId) => {
            setRequests(requests.filter(req => req.id !== requestId));
            setModalContent({ title: "Success", message: "Rejected request deleted." });
            setShowModal(true);
        };

        const handleRegisterUser = (data) => {
            const nextRole = registrationRoles[currentUser.role];
            let newUser = {
                id: `${nextRole.toLowerCase().slice(0, 3)}-${allUsers.length + 1}`,
                role: nextRole,
                fullName: data.fullName,
                phoneNumber: data.phoneNumber,
                password: data.password,
                regionName: currentUser.regionName,
                zoneName: currentUser.zoneName,
                woredaName: currentUser.woredaName,
                kebeleName: currentUser.kebeleName,
            };

            if (nextRole === 'Region') newUser.regionName = data.levelName;
            if (nextRole === 'Zone') newUser.zoneName = data.levelName;
            if (nextRole === 'Woreda') newUser.woredaName = data.levelName;
            if (nextRole === 'Kebele') newUser.kebeleName = data.levelName;

            setAllUsers([...allUsers, newUser]);
            setModalContent({ title: "Success", message: `${nextRole} Admin registered successfully!` });
            setShowModal(true);
            setView('dashboard');
        };

        const filteredRequests = requests.filter(req => {
            const matchesStatus = filterStatus === 'All' || req.previousApprovals[currentUser.role] === filterStatus;
            const matchesSearch = req.productName.toLowerCase().includes(searchQuery.toLowerCase()) ||
                req.id.toLowerCase().includes(searchQuery.toLowerCase());
            let matchesRole = false;

            if (currentUser) {
                // Federal sees all
                if (currentUser.role === 'Federal') {
                    matchesRole = true;
                } else if (currentUser.role === 'Region') {
                    matchesRole = req.regionName === currentUser.regionName;
                } else if (currentUser.role === 'Zone') {
                    matchesRole = req.zoneName === currentUser.zoneName;
                } else if (currentUser.role === 'Woreda') {
                    matchesRole = req.woredaName === currentUser.woredaName;
                } else if (currentUser.role === 'Kebele') {
                    matchesRole = req.kebeleName === currentUser.kebeleName;
                } else if (currentUser.role === 'Farmer') {
                    matchesRole = req.requesterId === currentUser.id;
                }
            }
            return matchesStatus && matchesSearch && matchesRole;
        });


        // ----------------------
        // Components
        // ----------------------
        const Modal = ({ show, onClose, title, message }) => {
            if (!show) return null;
            return ( <
                div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50" >
                <
                div className = "bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-sm text-center" >
                <
                h3 className = "text-xl font-bold mb-4 text-gray-900 dark:text-white" > { title } < /h3> <
                p className = "text-gray-700 dark:text-gray-300 mb-6" > { message } < /p> <
                button onClick = { onClose }
                className = "px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition" >
                Close <
                /button> < /
                div > <
                /div>
            );
        };

        const Login = () => {
                const {
                    register,
                    handleSubmit,
                    formState: {
                        errors
                    }
                } = useForm();
                return ( <
                        div className = "flex justify-center items-center h-screen bg-gray-100 dark:bg-gray-900" >
                        <
                        div className = "bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-md" >
                        <
                        h1 className = "text-3xl font-extrabold text-center mb-6 text-gray-900 dark:text-white" > Login < /h1> <
                        form onSubmit = { handleSubmit(handleLogin) }
                        className = "space-y-4" >
                        <
                        div >
                        <
                        input type = "text"
                        placeholder = "Phone Number" {...register("phoneNumber", { required: "Phone number is required" }) }
                        className = "w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all" /
                        >
                        {
                            errors.phoneNumber && < p className = "text-red-500 text-sm mt-1" > { errors.phoneNumber.message } < /p>} < /
                            div > <
                            div >
                            <
                            input
                            type = "password"
                            placeholder = "Password" {...register("password", { required: "Password is required" }) }
                            className = "w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all" /
                            >
                            {
                                errors.password && < p className = "text-red-500 text-sm mt-1" > { errors.password.message } < /p>} < /
                                div > <
                                button
                                type = "submit"
                                className = "w-full p-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition transform hover:scale-105 shadow-md" >
                                Login <
                                /button> < /
                                form > <
                                /div> < /
                                div >
                            );
                        };

                        const Header = () => ( <
                            header className = "bg-white dark:bg-gray-800 shadow-sm" >
                            <
                            div className = "container mx-auto px-4 py-4 flex justify-between items-center" >
                            <
                            div className = "flex items-center space-x-4" >
                            <
                            span className = "text-xl font-bold text-blue-600 dark:text-blue-400" > Request Hub < /span> <
                            span className = "text-sm text-gray-500 dark:text-gray-400 hidden sm:block" > { currentUser.role }: { currentUser.fullName } <
                            /span> < /
                            div > <
                            div className = "flex items-center space-x-4" > {
                                ['Federal', 'Region', 'Zone', 'Woreda', 'Kebele'].includes(currentUser.role) && ( <
                                    button onClick = {
                                        () => setView('registerUser')
                                    }
                                    className = "px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition shadow-sm" >
                                    <
                                    svg xmlns = "http://www.w3.org/2000/svg"
                                    className = "inline-block mr-2"
                                    width = "24"
                                    height = "24"
                                    viewBox = "0 0 24 24"
                                    fill = "none"
                                    stroke = "currentColor"
                                    strokeWidth = "2"
                                    strokeLinecap = "round"
                                    strokeLinejoin = "round" > < path d = "M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" > < /path><circle cx="8.5" cy="7" r="4"></circle > < path d = "M20 8v5" > < /path><path d="M17 12h6"></path > < /svg> Register User < /
                                    button >
                                )
                            } <
                            button onClick = { handleLogout }
                            className = "px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition shadow-sm" >
                            <
                            svg xmlns = "http://www.w3.org/2000/svg"
                            className = "inline-block mr-2"
                            width = "24"
                            height = "24"
                            viewBox = "0 0 24 24"
                            fill = "none"
                            stroke = "currentColor"
                            strokeWidth = "2"
                            strokeLinecap = "round"
                            strokeLinejoin = "round" > < path d = "M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" > < /path><polyline points="16 17 21 12 16 7"></polyline > < line x1 = "21"
                            y1 = "12"
                            x2 = "9"
                            y2 = "12" > < /line></svg > Logout <
                            /button> < /
                            div > <
                            /div> < /
                            header >
                        );

                        const RegisterUser = () => {
                                const { register, handleSubmit, formState: { errors }, watch } = useForm();
                                const password = watch("password", "");
                                const nextRole = registrationRoles[currentUser.role];

                                const getLevelName = () => {
                                    if (nextRole === 'Region') return 'Region Name';
                                    if (nextRole === 'Zone') return 'Zone Name';
                                    if (nextRole === 'Woreda') return 'Woreda Name';
                                    if (nextRole === 'Kebele') return 'Kebele Name';
                                    if (nextRole === 'Farmer') return 'Kebele Name';
                                    return 'Level Name';
                                };

                                return ( <
                                        div className = "flex justify-center items-center p-8 bg-gray-100 dark:bg-gray-900" >
                                        <
                                        div className = "bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-lg" >
                                        <
                                        h2 className = "text-2xl font-bold mb-6 text-gray-900 dark:text-white" > Register New { nextRole } < /h2> <
                                        form onSubmit = { handleSubmit(handleRegisterUser) }
                                        className = "space-y-4" >
                                        <
                                        div >
                                        <
                                        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Full Name < /label> <
                                        input type = "text"
                                        placeholder = "Full Name" {...register("fullName", { required: "Full name is required" }) }
                                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                        >
                                        {
                                            errors.fullName && < p className = "text-red-500 text-xs mt-1" > { errors.fullName.message } < /p>} < /
                                            div > <
                                            div >
                                            <
                                            label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Phone Number < /label> <
                                            input
                                            type = "text"
                                            placeholder = "Phone Number" {...register("phoneNumber", { required: "Phone number is required" }) }
                                            className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                            >
                                            {
                                                errors.phoneNumber && < p className = "text-red-500 text-xs mt-1" > { errors.phoneNumber.message } < /p>} < /
                                                div > <
                                                div >
                                                <
                                                label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Password < /label> <
                                                input
                                                type = "password"
                                                placeholder = "Password" {...register("password", { required: "Password is required" }) }
                                                className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                                >
                                                {
                                                    errors.password && < p className = "text-red-500 text-xs mt-1" > { errors.password.message } < /p>} < /
                                                    div > <
                                                    div >
                                                    <
                                                    label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Confirm Password < /label> <
                                                    input
                                                    type = "password"
                                                    placeholder = "Confirm Password" {...register("passwordConfirmation", {
                                                            required: "Please confirm your password",
                                                            validate: value => value === password || "The passwords do not match"
                                                        })
                                                    }
                                                    className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                                    >
                                                    {
                                                        errors.passwordConfirmation && < p className = "text-red-500 text-xs mt-1" > { errors.passwordConfirmation.message } < /p>} < /
                                                        div > <
                                                        div >
                                                        <
                                                        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > { getLevelName() } < /label> <
                                                        input
                                                        type = "text"
                                                        placeholder = { getLevelName() } {...register("levelName", { required: `${getLevelName()} is required` }) }
                                                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                                        >
                                                        {
                                                            errors.levelName && < p className = "text-red-500 text-xs mt-1" > { errors.levelName.message } < /p>} < /
                                                            div > <
                                                            button
                                                            type = "submit"
                                                            className = "w-full p-3 bg-purple-600 text-white font-semibold rounded-lg hover:bg-purple-700 transition transform hover:scale-105 shadow-md" >
                                                            Register <
                                                            /button> < /
                                                            form > <
                                                            /div> < /
                                                            div >
                                                        );
                                                    };

                                                    const ClientDashboard = () => {
                                                        const { register, handleSubmit, formState: { errors } } = useForm();
                                                        const clientRequests = requests.filter(req => req.requesterId === currentUser.id);

                                                        const onSubmit = (data) => {
                                                            const newRequest = {
                                                                ...data,
                                                                id: `REQ${Math.floor(10000 + Math.random() * 90000)}`,
                                                                requesterName: currentUser.fullName,
                                                                requesterId: currentUser.id,
                                                                status: 'Pending',
                                                                regionName: currentUser.regionName,
                                                                zoneName: currentUser.zoneName,
                                                                woredaName: currentUser.woredaName,
                                                                kebeleName: currentUser.kebeleName,
                                                                previousApprovals: {
                                                                    Federal: 'Pending',
                                                                    Region: 'Pending',
                                                                    Zone: 'Pending',
                                                                    Woreda: 'Pending',
                                                                    Kebele: 'Pending',
                                                                },
                                                            };
                                                            setRequests([...requests, newRequest]);
                                                            setModalContent({ title: "Request Submitted", message: "Your request has been submitted successfully." });
                                                            setShowModal(true);
                                                        };

                                                        const getStatusColor = (status) => {
                                                            switch (status) {
                                                                case 'Pending':
                                                                    return 'bg-yellow-100 text-yellow-800';
                                                                case 'Approved':
                                                                    return 'bg-blue-100 text-blue-800';
                                                                case 'Accepted':
                                                                    return 'bg-green-100 text-green-800';
                                                                case 'Rejected':
                                                                    return 'bg-red-100 text-red-800';
                                                                default:
                                                                    return 'bg-gray-100 text-gray-800';
                                                            }
                                                        };

                                                        return ( <
                                                            div className = "container mx-auto p-4 space-y-8" >
                                                            <
                                                            div className = "grid md:grid-cols-2 gap-8" >
                                                            <
                                                            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                            <
                                                            h2 className = "text-2xl font-bold mb-4 text-gray-900 dark:text-white" > Submit New Request < /h2> <
                                                            form onSubmit = { handleSubmit(onSubmit) }
                                                            className = "space-y-4" >
                                                            <
                                                            div >
                                                            <
                                                            label htmlFor = "productName"
                                                            className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Product Name < /label> <
                                                            input type = "text"
                                                            id = "productName"
                                                            placeholder = "e.g., Fertilizer" {...register("productName", { required: "Product name is required" }) }
                                                            className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                                            >
                                                            {
                                                                errors.productName && < p className = "text-red-500 text-xs mt-1" > { errors.productName.message } < /p>} < /
                                                                div > <
                                                                div >
                                                                <
                                                                label htmlFor = "amount"
                                                                className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Amount < /label> <
                                                                input
                                                                type = "number"
                                                                id = "amount"
                                                                placeholder = "e.g., 100" {...register("amount", { required: "Amount is required", min: { value: 1, message: "Amount must be at least 1" } }) }
                                                                className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" /
                                                                >
                                                                {
                                                                    errors.amount && < p className = "text-red-500 text-xs mt-1" > { errors.amount.message } < /p>} < /
                                                                    div > <
                                                                    div >
                                                                    <
                                                                    label htmlFor = "productPurpose"
                                                                    className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Purpose < /label> <
                                                                    textarea
                                                                    id = "productPurpose"
                                                                    placeholder = "e.g., For land preparation for the new season" {...register("productPurpose") }
                                                                    className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" >
                                                                    <
                                                                    /textarea> < /
                                                                    div > <
                                                                    button
                                                                    type = "submit"
                                                                    className = "w-full p-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
                                                                    Submit Request <
                                                                    /button> < /
                                                                    form > <
                                                                    /div> <
                                                                    div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                                                                    <
                                                                    h2 className = "text-2xl font-bold mb-4 text-gray-900 dark:text-white" > My Request Status < /h2> {
                                                                    clientRequests.length > 0 ? ( <
                                                                        ul className = "divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                            clientRequests.map(request => ( <
                                                                                li key = { request.id }
                                                                                className = "py-4 space-y-2" >
                                                                                <
                                                                                div className = "flex justify-between items-center" >
                                                                                <
                                                                                div className = "text-lg font-medium text-gray-900 dark:text-white" > { request.productName } < /div> <
                                                                                span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(request.status)}` } > { request.status } <
                                                                                /span> < /
                                                                                div > <
                                                                                div className = "text-sm text-gray-500 dark:text-gray-400" > Request ID: { request.id } < /div> <
                                                                                div className = "flex flex-wrap gap-2 text-sm" > {
                                                                                    Object.entries(request.previousApprovals).map(([level, status]) => ( <
                                                                                        span key = { level }
                                                                                        className = { `px-2 py-1 rounded-full text-xs font-semibold ${getStatusColor(status)}` } > { level }: { status } <
                                                                                        /span>
                                                                                    ))
                                                                                } <
                                                                                /div> < /
                                                                                li >
                                                                            ))
                                                                        } <
                                                                        /ul>
                                                                    ) : ( <
                                                                        p className = "text-gray-500 dark:text-gray-400" > You have no requests yet. < /p>
                                                                    )
                                                                } <
                                                                /div> < /
                                                                div > <
                                                                /div>
                                                            );
                                                        };

                                                        const AdminDashboard = () => {
                                                            const getStatusColor = (status) => {
                                                                switch (status) {
                                                                    case 'Pending':
                                                                        return 'bg-yellow-100 text-yellow-800';
                                                                    case 'Approved':
                                                                        return 'bg-blue-100 text-blue-800';
                                                                    case 'Accepted':
                                                                        return 'bg-green-100 text-green-800';
                                                                    case 'Rejected':
                                                                        return 'bg-red-100 text-red-800';
                                                                    default:
                                                                        return 'bg-gray-100 text-gray-800';
                                                                }
                                                            };

                                                            return ( <
                                                                div className = "container mx-auto p-4 space-y-8" >
                                                                <
                                                                div className = "flex flex-col sm:flex-row justify-between items-center mb-4 space-y-4 sm:space-y-0" >
                                                                <
                                                                div className = "flex space-x-2" >
                                                                <
                                                                input type = "text"
                                                                placeholder = "Search by ID or Product Name..."
                                                                value = { searchQuery }
                                                                onChange = {
                                                                    (e) => setSearchQuery(e.target.value)
                                                                }
                                                                className = "p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500" /
                                                                >
                                                                <
                                                                button onClick = {
                                                                    () => {}
                                                                }
                                                                className = "p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition" >
                                                                <
                                                                svg xmlns = "http://www.w3.org/2000/svg"
                                                                width = "24"
                                                                height = "24"
                                                                viewBox = "0 0 24 24"
                                                                fill = "none"
                                                                stroke = "currentColor"
                                                                strokeWidth = "2"
                                                                strokeLinecap = "round"
                                                                strokeLinejoin = "round" > < circle cx = "11"
                                                                cy = "11"
                                                                r = "8" > < /circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line > < /svg> < /
                                                                button > <
                                                                /div> <
                                                                div className = "flex space-x-2" >
                                                                <
                                                                svg xmlns = "http://www.w3.org/2000/svg"
                                                                className = "text-gray-500 dark:text-gray-400 text-xl"
                                                                width = "24"
                                                                height = "24"
                                                                viewBox = "0 0 24 24"
                                                                fill = "none"
                                                                stroke = "currentColor"
                                                                strokeWidth = "2"
                                                                strokeLinecap = "round"
                                                                strokeLinejoin = "round" > < polygon points = "22 3 2 3 10 12.46 10 19 14 21 14 12.46 22 3" > < /polygon></svg > {
                                                                    ['All', 'Pending', 'Approved', 'Accepted', 'Rejected'].map(status => ( <
                                                                        button key = { status }
                                                                        onClick = {
                                                                            () => setFilterStatus(status)
                                                                        }
                                                                        className = { `px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                                    filterStatus === status
                                        ? 'bg-blue-600 text-white shadow-md'
                                        : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600'
                                }` } > { status } <
                                                                        /button>
                                                                    ))
                                                                } <
                                                                /div> < /
                                                                div >

                                                                <
                                                                div className = "bg-white dark:bg-gray-800 rounded-xl shadow-md overflow-hidden" >
                                                                <
                                                                table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                                                                <
                                                                thead className = "bg-gray-50 dark:bg-gray-700" >
                                                                <
                                                                tr >
                                                                <
                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > ID < /th> <
                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product < /th> <
                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status({ currentUser.role }) < /th> <
                                                                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Actions < /th> < /
                                                                tr > <
                                                                /thead> <
                                                                tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                                                                    filteredRequests.map(req => ( <
                                                                        tr key = { req.id } >
                                                                        <
                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.id } < /td> <
                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.productName } < /td> <
                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                                                                        td className = "px-6 py-4 whitespace-nowrap" >
                                                                        <
                                                                        span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(req.previousApprovals[currentUser.role])}` } > { req.previousApprovals[currentUser.role] } <
                                                                        /span> < /
                                                                        td > <
                                                                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2" > {
                                                                            req.previousApprovals[currentUser.role] === 'Pending' && ( <
                                                                                >
                                                                                <
                                                                                button onClick = {
                                                                                    () => handleApproveReject(req.id, 'Approved')
                                                                                }
                                                                                className = "text-blue-600 hover:text-blue-900"
                                                                                title = "Approve (Pass to next level)" >
                                                                                <
                                                                                svg xmlns = "http://www.w3.org/2000/svg"
                                                                                className = "inline-block text-xl"
                                                                                width = "24"
                                                                                height = "24"
                                                                                viewBox = "0 0 24 24"
                                                                                fill = "none"
                                                                                stroke = "currentColor"
                                                                                strokeWidth = "2"
                                                                                strokeLinecap = "round"
                                                                                strokeLinejoin = "round" > < path d = "M5 12h14" > < /path><path d="m12 5 7 7-7 7"></path > < /svg> < /
                                                                                button > <
                                                                                button onClick = {
                                                                                    () => handleApproveReject(req.id, 'Accepted')
                                                                                }
                                                                                className = "text-green-600 hover:text-green-900"
                                                                                title = "Accept (Supported at this level)" >
                                                                                <
                                                                                svg xmlns = "http://www.w3.org/2000/svg"
                                                                                className = "inline-block text-xl"
                                                                                width = "24"
                                                                                height = "24"
                                                                                viewBox = "0 0 24 24"
                                                                                fill = "none"
                                                                                stroke = "currentColor"
                                                                                strokeWidth = "2"
                                                                                strokeLinecap = "round"
                                                                                strokeLinejoin = "round" > < polyline points = "20 6 9 17 4 12" > < /polyline></svg >
                                                                                <
                                                                                /button> <
                                                                                button onClick = {
                                                                                    () => handleApproveReject(req.id, 'Rejected')
                                                                                }
                                                                                className = "text-red-600 hover:text-red-900"
                                                                                title = "Reject (Unsupported)" >
                                                                                <
                                                                                svg xmlns = "http://www.w3.org/2000/svg"
                                                                                className = "inline-block text-xl"
                                                                                width = "24"
                                                                                height = "24"
                                                                                viewBox = "0 0 24 24"
                                                                                fill = "none"
                                                                                stroke = "currentColor"
                                                                                strokeWidth = "2"
                                                                                strokeLinecap = "round"
                                                                                strokeLinejoin = "round" > < circle cx = "12"
                                                                                cy = "12"
                                                                                r = "10" > < /circle><line x1="15" y1="9" x2="9" y2="15"></line > < line x1 = "9"
                                                                                y1 = "9"
                                                                                x2 = "15"
                                                                                y2 = "15" > < /line></svg >
                                                                                <
                                                                                /button> < /
                                                                                >
                                                                            )
                                                                        } {
                                                                            req.previousApprovals[currentUser.role] === 'Rejected' && ( <
                                                                                button onClick = {
                                                                                    () => handleDeleteRequest(req.id)
                                                                                }
                                                                                className = "text-gray-600 hover:text-gray-900"
                                                                                title = "Delete Rejected Request" >
                                                                                <
                                                                                svg xmlns = "http://www.w3.org/2000/svg"
                                                                                className = "inline-block text-xl"
                                                                                width = "24"
                                                                                height = "24"
                                                                                viewBox = "0 0 24 24"
                                                                                fill = "none"
                                                                                stroke = "currentColor"
                                                                                strokeWidth = "2"
                                                                                strokeLinecap = "round"
                                                                                strokeLinejoin = "round" > < polyline points = "3 6 5 6 21 6" > < /polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path > < line x1 = "10"
                                                                                y1 = "11"
                                                                                x2 = "10"
                                                                                y2 = "17" > < /line><line x1="14" y1="11" x2="14" y2="17"></line > < /svg> < /
                                                                                button >
                                                                            )
                                                                        } <
                                                                        /td> < /
                                                                        tr >
                                                                    ))
                                                                } <
                                                                /tbody> < /
                                                                table > <
                                                                /div> < /
                                                                div >
                                                            );
                                                        };

                                                        if (!currentUser) {
                                                            return <Login / > ;
                                                        }

                                                        return ( <
                                                            div className = "bg-gray-100 dark:bg-gray-900 min-h-screen font-sans antialiased" >
                                                            <
                                                            Modal show = { showModal }
                                                            onClose = {
                                                                () => setShowModal(false)
                                                            }
                                                            title = { modalContent.title }
                                                            message = { modalContent.message }
                                                            /> <
                                                            Header / >
                                                            <
                                                            main > { view === 'login' && < Login / > } { view === 'dashboard' && currentUser.role === 'Farmer' && < ClientDashboard / > } { view === 'dashboard' && currentUser.role !== 'Farmer' && < AdminDashboard / > } { view === 'registerUser' && ['Federal', 'Region', 'Zone', 'Woreda', 'Kebele'].includes(currentUser.role) && < RegisterUser / > } <
                                                            /main> < /
                                                            div >
                                                        );
                                                    };

                                                    export default App;
