import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './KebeleDashboard.css';

const KebeleDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const navigate = useNavigate();

        // Farmer Management State
        const [farmers, setFarmers] = useState([]);
        const [showFarmerModal, setShowFarmerModal] = useState(false);
        const [editingFarmer, setEditingFarmer] = useState(null);
        const [farmerForm, setFarmerForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: ''
        });

        // Product Management State
        const [products, setProducts] = useState([]);
        const [showProductModal, setShowProductModal] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            description: '',
            category: '',
            amount: ''
        });

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [selectedRequests, setSelectedRequests] = useState([]);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');
        const [bulkAction, setBulkAction] = useState('');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        useEffect(() => {
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Kebele' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Farmers
                const farmersResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (farmersResponse.ok) {
                    const data = await farmersResponse.json();
                    const farmersData = data.filter(item => item.type === 'farmer');
                    setFarmers(farmersData);
                    setStats(prev => ({...prev, totalFarmers: farmersData.length }));
                }

                // Products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Requests
                const requestsResponse = await fetch('http://localhost:5000/api/approval-workflow/requests/status', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);

                    const totalRequests = requestsData.length;
                    const pendingRequests = requestsData.filter(r => r.status === 'Pending').length;
                    const approvedRequests = requestsData.filter(r => ['Approved', 'Accepted', 'Kebele_Approved', 'Woreda_Approved', 'Zone_Approved', 'Region_Approved'].includes(r.status)).length;
                    const rejectedRequests = requestsData.filter(r => r.status === 'Rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        rejectedRequests
                    }));
                }
            } catch (err) {
                console.error('Error fetching dashboard data:', err);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // --- Farmer management functions (same as before) ---
        // ... keep your handleFarmerSubmit, handleEditFarmer, handleDeleteFarmer here ...

        // --- Product management functions (same as before) ---
        // ... keep handleProductSubmit, handleEditProduct, handleDeleteProduct here ...

        // --- Request Management ---
        const updateRequestStatus = async(requestId, status, feedback = '') => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(
                    `http://localhost:5000/api/approval-workflow/requests/${requestId}/status/level`, {
                        method: 'PUT',
                        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
                        body: JSON.stringify({ status, feedback })
                    }
                );
                const data = await response.json();
                if (response.ok) {
                    setShowRequestModal(false);
                    setSelectedRequest(null);
                    setRequestStatus('');
                    setDecisionReason('');
                    fetchDashboardData();
                    alert(`Request ${status} successfully`);
                } else {
                    alert(data.message || 'Error updating request');
                }
            } catch (err) {
                console.error('updateRequestStatus error:', err);
            }
        };

        const handleBulkUpdate = async() => {
            if (!bulkAction) {
                alert('Select Approve or Reject first');
                return;
            }
            if (selectedRequests.length === 0) {
                alert('No requests selected');
                return;
            }
            if (!window.confirm(`Are you sure you want to ${bulkAction} ${selectedRequests.length} requests?`)) return;

            for (const requestId of selectedRequests) {
                await updateRequestStatus(requestId, bulkAction, decisionReason);
            }
            setSelectedRequests([]);
            setBulkAction('');
            setDecisionReason('');
            fetchDashboardData();
        };

        const getStatusBadge = status => {
            const statusClasses = {
                Pending: 'status-pending',
                Approved: 'status-approved',
                Accepted: 'status-accepted',
                Rejected: 'status-rejected',
                Kebele_Approved: 'status-kebele-approved',
                Woreda_Approved: 'status-woreda-approved',
                Zone_Approved: 'status-zone-approved',
                Region_Approved: 'status-region-approved'
            };
            const format = s => s.replace(/_/g, ' ').replace(/\b\w/g, c => c.toUpperCase());
            return <span className = { `status-badge ${statusClasses[status] || ''}` } > { format(status) } < /span>;
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];
            return ( <
                div className = "level-status" >
                <
                h4 > { level.charAt(0).toUpperCase() + level.slice(1) }
                Level < /h4> <
                p > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p > < strong > Admin: < /strong> {admin}</p > } { feedback && < p > < strong > Feedback: < /strong> {feedback}</p > } { date && < p > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        if (!user) return <div className = "loading" > Loading... < /div>;

        return ( <
            div className = "kebele-dashboard" > { /* Sidebar */ } { /* ... keep sidebar buttons as in your code ... */ }

            { /* Main content */ } <
            div className = "main-content" >
            <
            header >
            <
            h1 > Welcome, { user.fullName }(Kebele Admin) < /h1> <
            button onClick = { handleLogout }
            className = "logout-btn" > Logout < /button> <
            /header>

            <
            main > { /* Requests Tab */ } {
                activeTab === 'requests' && ( <
                        div className = "requests-tab" >
                        <
                        div className = "tab-header" >
                        <
                        h2 > Request Management < /h2> <
                        p > Manage farmer requests in your kebele: { user.kebele_name } < /p> <
                        /div>

                        <
                        div className = "requests-list" > {
                            requests.map(request => ( <
                                    div key = { request.id }
                                    className = "request-card" >
                                    <
                                    div className = "request-info" >
                                    <
                                    h3 > { request.product_name } < /h3> <
                                    p > üë®‚ÄçüåæFarmer: { request.farmer_name }({ request.farmer_phone }) < /p> <
                                    p > üì¶Quantity: { request.quantity } < /p> {
                                        request.note && < p > üìùNote: { request.note } < /p>} <
                                            p > üí∞Product Amount: { request.product_amount } < /p> <
                                            p > üìÖDate: { new Date(request.created_at).toLocaleDateString() } < /p> { getStatusBadge(request.status) } <
                                            /div>

                                        <
                                        div className = "approval-levels" > { getLevelStatus('kebele', request) } { getLevelStatus('woreda', request) } { getLevelStatus('zone', request) } { getLevelStatus('region', request) } { getLevelStatus('federal', request) } <
                                            /div> <
                                            /div>
                                    ))
                            } <
                            /div> <
                            /div>
                        )
                    } <
                    /main> <
                    /div> <
                    /div>
            );
        };

        export default KebeleDashboard;
