import React from 'react';
import { Table, Tag, Space, Button } from 'antd';

const RequestsTable = ({ requests, onApprove, onReject }) => {
    const columns = [{
            title: 'Request ID',
            dataIndex: 'id',
            key: 'id',
        },
        {
            title: 'Requester',
            dataIndex: 'requester',
            key: 'requester',
        },
        {
            title: 'Date',
            dataIndex: 'date',
            key: 'date',
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            render: status => ( <
                Tag color = { status === 'Pending' ? 'orange' : status === 'Approved' ? 'green' : 'red' } > { status } <
                /Tag>
            ),
        },
        {
            title: 'Action',
            key: 'action',
            render: (_, record) => ( <
                Space size = "middle" >
                <
                Button type = "primary"
                onClick = {
                    () => onApprove(record.id) }
                disabled = { record.status !== 'Pending' } >
                Approve <
                /Button> <
                Button danger onClick = {
                    () => onReject(record.id) }
                disabled = { record.status !== 'Pending' } >
                Reject <
                /Button> <
                /Space>
            ),
        },
    ];

    return <Table columns = { columns }
    dataSource = { requests }
    rowKey = "id" / > ;
};

export default RequestsTable;
