import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, collection, query, where, onSnapshot, serverTimestamp, addDoc, updateDoc, getDocs, deleteDoc } from 'firebase/firestore';
import { useForm } from 'react-hook-form';
import './index.css';

// The following global variables are provided by the Canvas environment.
// For local development, we provide fallback values to avoid compilation errors.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {
    // You can replace these with your own Firebase project configuration for full functionality
    apiKey: "AIzaSyBh7ErxnKurtNkeK_0PW5XW4joDe_r9Ut4",
    authDomain: "farmers-management-syste-84643.firebaseapp.com",
    projectId: "farmers-management-syste-84643",
    storageBucket: "farmers-management-syste-84643.firebasestorage.app",
    messagingSenderId: "41077942495"
};
const __initial_auth_token = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Initialize Firebase with the provided config
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// ----------------------
// Sidebar Component
// ----------------------
// A standalone Sidebar component to be rendered in App.js
const Sidebar = ({ isCompressed, onToggleSidebar, onNavigate, onToggleTheme }) => {
        const sidebarItems = [
            { id: 'dashboard', label: 'Dashboard', icon: 'M12 2L2 7l10 5 10-5-10-5zm0 14.5v-9L2 12l10 5.5z' },
            { id: 'master-data', label: 'Master Data', icon: 'M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z' },
            { id: 'reports', label: 'Reports', icon: 'M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6zM6 20V4h7v4h4v12H6z' },
            { id: 'registration', label: 'Register User', icon: 'M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z' }
        ];

        return ( <
                aside className = { `h-screen bg-gray-100 dark:bg-gray-800 text-gray-800 dark:text-white transition-all duration-300 ease-in-out ${isCompressed ? 'w-20' : 'w-64'} p-4 flex flex-col rounded-xl shadow-lg` } >
                <
                div className = "flex items-center justify-between mb-8" > {!isCompressed && < h1 className = "text-2xl font-bold" > Menu < /h1>} <
                    button onClick = { onToggleSidebar }
                    className = "p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors" >
                    <
                    svg className = "w-6 h-6"
                    fill = "currentColor"
                    viewBox = "0 0 24 24"
                    xmlns = "http://www.w3.org/2000/svg" >
                    <
                    path d = { isCompressed ? "M4 6h16M4 12h16M4 18h16" : "M4 6h16M4 12h16M4 18h16" }
                    /> <
                    /svg> <
                    /button> <
                    /div>

                    <
                    nav className = "flex-1" >
                    <
                    ul > {
                        sidebarItems.map(item => ( <
                                li key = { item.id }
                                className = "mb-2" >
                                <
                                button onClick = {
                                    () => onNavigate(item.id) }
                                className = "flex items-center w-full p-3 rounded-lg text-left transition-colors hover:bg-gray-200 dark:hover:bg-gray-700" >
                                <
                                svg className = "w-6 h-6 mr-3"
                                fill = "currentColor"
                                viewBox = "0 0 24 24"
                                xmlns = "http://www.w3.org/2000/svg" >
                                <
                                path d = { item.icon }
                                /> <
                                /svg> {
                                    !isCompressed && < span className = "font-medium" > { item.label } < /span>} <
                                        /button> <
                                        /li>
                                ))
                        } <
                        /ul> <
                        /nav>

                        <
                        div className = "mt-auto" >
                        <
                        button
                        onClick = { onToggleTheme }
                        className = "w-full flex items-center p-3 rounded-lg text-left transition-colors hover:bg-gray-200 dark:hover:bg-gray-700" >
                        <
                        svg className = "w-6 h-6 mr-3"
                        fill = "currentColor"
                        viewBox = "0 0 24 24"
                        xmlns = "http://www.w3.org/2000/svg" >
                        <
                        path d = "M12 2a10 10 0 100 20A10 10 0 0012 2zm-1 16a7.995 7.995 0 01-6.72-3.78A7.995 7.995 0 0111 4.72V18z" / >
                        <
                        /svg> {
                            !isCompressed && < span > Dark Mode < /span>} <
                                /button> <
                                /div> <
                                /aside>
                        );
                    };

                    // ----------------------
                    // Dashboard Components (Placeholders)
                    // ----------------------
                    // These components are placeholders for the actual dashboards.
                    // They will be updated to fetch data from the MySQL backend.
                    const MainDashboard = () => {
                        // This dashboard would fetch summary data from your backend.
                        return <div className = "p-8" >
                            <
                            h2 className = "text-3xl font-bold mb-4 dark:text-white" > Main Dashboard < /h2> <
                            p className = "text-gray-600 dark:text-gray-400" > Welcome to your dashboard.This page provides an overview of your data. < /p> <
                            div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6" >
                            <
                            div className = "bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg" >
                            <
                            h3 className = "text-xl font-semibold text-gray-800 dark:text-white" > Total Users < /h3> <
                            p className = "text-3xl font-bold mt-2 text-blue-500" > 1, 250 < /p> <
                            /div> <
                            div className = "bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg" >
                            <
                            h3 className = "text-xl font-semibold text-gray-800 dark:text-white" > Active Products < /h3> <
                            p className = "text-3xl font-bold mt-2 text-green-500" > 250 < /p> <
                            /div> <
                            div className = "bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg" >
                            <
                            h3 className = "text-xl font-semibold text-gray-800 dark:text-white" > Pending Requests < /h3> <
                            p className = "text-3xl font-bold mt-2 text-yellow-500" > 15 < /p> <
                            /div> <
                            /div> <
                            /div>;
                    };

                    const MasterData = () => {
                        const [products, setProducts] = useState([]);
                        const [users, setUsers] = useState([]);
                        const [showDeleteModal, setShowDeleteModal] = useState(false);
                        const [itemToDelete, setItemToDelete] = useState(null);
                        const [itemType, setItemType] = useState('');

                        useEffect(() => {
                            // Simulating API calls to a MySQL backend
                            const fetchMasterData = async() => {
                                // Fetch products
                                try {
                                    const productsResponse = await fetch('/api/products'); // Hypothetical endpoint
                                    const productsData = await productsResponse.json();
                                    setProducts(productsData);
                                } catch (error) {
                                    console.error('Error fetching products:', error);
                                }

                                // Fetch users
                                try {
                                    const usersResponse = await fetch('/api/users'); // Hypothetical endpoint
                                    const usersData = await usersResponse.json();
                                    setUsers(usersData);
                                } catch (error) {
                                    console.error('Error fetching users:', error);
                                }
                            };

                            fetchMasterData();
                        }, []);

                        const handleDeleteClick = (item, type) => {
                            setItemToDelete(item);
                            setItemType(type);
                            setShowDeleteModal(true);
                        };

                        const confirmDeletion = async() => {
                            if (!itemToDelete) return;

                            try {
                                const endpoint = itemType === 'product' ? `/api/products/${itemToDelete.id}` : `/api/users/${itemToDelete.id}`;
                                const response = await fetch(endpoint, { method: 'DELETE' }); // Hypothetical DELETE request
                                if (response.ok) {
                                    console.log(`${itemType} deleted successfully.`);
                                    // Update the state to reflect the deletion
                                    if (itemType === 'product') {
                                        setProducts(products.filter(p => p.id !== itemToDelete.id));
                                    } else {
                                        setUsers(users.filter(u => u.id !== itemToDelete.id));
                                    }
                                } else {
                                    console.error(`Failed to delete ${itemType}.`);
                                }
                            } catch (error) {
                                console.error('Error during deletion:', error);
                            } finally {
                                setShowDeleteModal(false);
                                setItemToDelete(null);
                                setItemType('');
                            }
                        };

                        return ( <
                            div className = "p-8" >
                            <
                            h2 className = "text-3xl font-bold mb-6 dark:text-white" > Master Data Management < /h2> <
                            div className = "grid grid-cols-1 lg:grid-cols-2 gap-8" > { /* Products Section */ } <
                            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
                            <
                            h3 className = "text-2xl font-semibold mb-4 dark:text-white" > Products < /h3> <
                            ul className = "space-y-4" > {
                                products.map(product => ( <
                                    li key = { product.id }
                                    className = "flex items-center justify-between p-4 bg-gray-100 dark:bg-gray-700 rounded-lg" >
                                    <
                                    span className = "font-medium text-gray-800 dark:text-white" > { product.name } < /span> <
                                    button onClick = {
                                        () => handleDeleteClick(product, 'product') }
                                    className = "bg-red-500 text-white p-2 rounded-full hover:bg-red-600 transition-colors" >
                                    <
                                    svg className = "w-5 h-5"
                                    fill = "currentColor"
                                    viewBox = "0 0 24 24"
                                    xmlns = "http://www.w3.org/2000/svg" >
                                    <
                                    path d = "M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" / >
                                    <
                                    /svg> <
                                    /button> <
                                    /li>
                                ))
                            } <
                            /ul> <
                            /div>

                            { /* Users Section */ } <
                            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg" >
                            <
                            h3 className = "text-2xl font-semibold mb-4 dark:text-white" > Users < /h3> <
                            ul className = "space-y-4" > {
                                users.map(user => ( <
                                    li key = { user.id }
                                    className = "flex items-center justify-between p-4 bg-gray-100 dark:bg-gray-700 rounded-lg" >
                                    <
                                    span className = "font-medium text-gray-800 dark:text-white" > { user.name } < /span> <
                                    button onClick = {
                                        () => handleDeleteClick(user, 'user') }
                                    className = "bg-red-500 text-white p-2 rounded-full hover:bg-red-600 transition-colors" >
                                    <
                                    svg className = "w-5 h-5"
                                    fill = "currentColor"
                                    viewBox = "0 0 24 24"
                                    xmlns = "http://www.w3.org/2000/svg" >
                                    <
                                    path d = "M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z" / >
                                    <
                                    /svg> <
                                    /button> <
                                    /li>
                                ))
                            } <
                            /ul> <
