                                    <
                                    div >
                                    <
                                    label className = "block text-gray-700 dark:text-gray-300 font-bold mb-2" > Username < /label> <
                                    input type = "text"
                                    className = "w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-100 dark:bg-gray-700 dark:text-white" {...register("username", { required: "Username is required" }) }
                                    /> {
