import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const FarmerDashboard = () => {
  const [activeTab, setActiveTab] = useState('requests');
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // State
  const [requests, setRequests] = useState([]);
  const [deliveries, setDeliveries] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showRequestModal, setShowRequestModal] = useState(false);
  const [showStatusModal, setShowStatusModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [editingRequest, setEditingRequest] = useState(null);
  const [requestForm, setRequestForm] = useState({
    product_id: '',
    quantity: '',
    note: ''
  });

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = JSON.parse(localStorage.getItem('user') || 'null');
    const userType = localStorage.getItem('userType');

    if (!token || userType !== 'Farmer' || !userData) {
      navigate('/login');
      return;
    }

    setUser(userData);
    fetchDashboardData();
  }, [navigate]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Products
      const prodRes = await fetch('http://localhost:5000/api/farmers/products', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (prodRes.ok) setProducts(await prodRes.json());

      // Requests
      const reqRes = await fetch('http://localhost:5000/api/farmers/requests', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (reqRes.ok) setRequests(await reqRes.json());

      // Deliveries
      const delRes = await fetch('http://localhost:5000/api/farmers/deliveries', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (delRes.ok) setDeliveries(await delRes.json());
    } catch (err) {
      setError('Error loading dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const fetchRequestStatusDetail = async (requestId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/farmers/request/${requestId}/status`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.ok) {
        setSelectedRequest(await res.json());
        setShowStatusModal(true);
      }
    } catch (err) {
      alert('Error fetching status details');
    }
  };

  const confirmDelivery = async (requestId) => {
    if (!window.confirm('Do you confirm you received this product?')) return;
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/farmers/requests/${requestId}/confirm-delivery`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({ note: 'Farmer confirmed delivery' })
      });
      const data = await res.json();
      if (res.ok) {
        alert('Delivery confirmed.');
        fetchDashboardData();
      } else {
        alert(data.message || 'Error confirming delivery');
      }
    } catch (err) {
      alert('Error confirming delivery');
    }
  };

  const handleRequestSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!requestForm.product_id || !requestForm.quantity) return alert('Product and quantity required');
      if (requestForm.quantity <= 0) return alert('Quantity must be greater than 0');

      const url = editingRequest
        ? `http://localhost:5000/api/farmers/request/${editingRequest.id}`
        : 'http://localhost:5000/api/farmers/request';
      const method = editingRequest ? 'PUT' : 'POST';

      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({
          product_id: parseInt(requestForm.product_id),
          quantity: parseInt(requestForm.quantity),
          note: requestForm.note
        })
      });

      if (res.ok) {
        alert(editingRequest ? 'Request updated' : 'Request created');
        setShowRequestModal(false);
        setEditingRequest(null);
        setRequestForm({ product_id: '', quantity: '', note: '' });
        fetchDashboardData();
      }
    } catch {
      alert('Error processing request');
    }
  };

  const handleEditRequest = (req) => {
    // Only editable if all levels still pending
    if (
      req.kebele_status !== 'Pending' ||
      req.woreda_status !== 'Pending' ||
      req.zone_status !== 'Pending' ||
      req.region_status !== 'Pending' ||
      req.federal_status !== 'Pending'
    ) {
      return alert('Cannot edit: request already processed at kebele or higher level.');
    }
    setEditingRequest(req);
    setRequestForm({
      product_id: req.product_id.toString(),
      quantity: req.quantity.toString(),
      note: req.note || ''
    });
    setShowRequestModal(true);
  };

  const handleDeleteRequest = async (requestId, req) => {
    if (
      req.kebele_status !== 'Pending' ||
      req.woreda_status !== 'Pending' ||
      req.zone_status !== 'Pending' ||
      req.region_status !== 'Pending' ||
      req.federal_status !== 'Pending'
    ) {
      return alert('Cannot delete: request already processed at kebele or higher level.');
    }

    if (!window.confirm('Delete this request?')) return;
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/farmers/request/${requestId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.ok) {
        alert('Request deleted');
        fetchDashboardData();
      }
    } catch {
      alert('Error deleting request');
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const getStatusBadge = (status) => {
    const classes = {
      Pending: 'bg-yellow-200 text-yellow-800',
      Accepted: 'bg-green-200 text-green-800',
      Rejected: 'bg-red-200 text-red-800',
      Approved: 'bg-blue-200 text-blue-800'
    };
    return <span className={`px-2 py-1 rounded ${classes[status] || 'bg-gray-200'}`}>{status}</span>;
  };

  if (loading) return <div className="p-6">Loading...</div>;
  if (error) return <div className="p-6 text-red-500">{error}</div>;

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-gray-100 p-4">
        <h2 className="text-xl font-bold mb-4">Farmer Dashboard</h2>
        <nav className="space-y-2">
          <button className={`block w-full text-left ${activeTab === 'requests' && 'font-bold'}`} onClick={() => setActiveTab('requests')}>üìã My Requests</button>
          <button className={`block w-full text-left ${activeTab === 'products' && 'font-bold'}`} onClick={() => setActiveTab('products')}>üõí Products</button>
          <button className={`block w-full text-left ${activeTab === 'deliveries' && 'font-bold'}`} onClick={() => setActiveTab('deliveries')}>üöö Deliveries</button>
        </nav>
        <button onClick={handleLogout} className="mt-6 bg-red-500 text-white px-4 py-2 rounded">Logout</button>
      </div>

      {/* Main content */}
      <div className="flex-1 p-6 overflow-y-auto">
        <h1 className="text-2xl mb-6">Welcome, {user.fullName} (Farmer)</h1>

        {/* Requests */}
        {activeTab === 'requests' && (
          <div>
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-bold">My Requests</h2>
              <button className="bg-blue-500 text-white px-3 py-1 rounded" onClick={() => setShowRequestModal(true)}>‚ûï New Request</button>
            </div>
            <div className="space-y-4">
              {requests.map((r) => (
                <div key={r.id} className="border p-4 rounded shadow">
                  <h3 className="font-bold">{r.product_name}</h3>
                  <p>Qty: {r.quantity}</p>
                  <p>Date: {new Date(r.created_at).toLocaleDateString()}</p>
                  {getStatusBadge(r.status)}
                  <div className="mt-2 space-x-2">
                    <button className="bg-gray-200 px-2 py-1 rounded" onClick={() => fetchRequestStatusDetail(r.id)}>üìä Status</button>
                    <button className="bg-yellow-200 px-2 py-1 rounded" onClick={() => handleEditRequest(r)}>‚úèÔ∏è Edit</button>
                    <button className="bg-red-200 px-2 py-1 rounded" onClick={() => handleDeleteRequest(r.id, r)}>üóëÔ∏è Delete</button>
                    {(r.status === 'Accepted' ||
                      r.kebele_status === 'Accepted' ||
                      r.woreda_status === 'Accepted' ||
                      r.zone_status === 'Accepted' ||
                      r.region_status === 'Accepted' ||
                      r.federal_status === 'Accepted') && (
                      <button className="bg-green-200 px-2 py-1 rounded" onClick={() => confirmDelivery(r.id)}>‚úÖ Confirm Delivery</button>
                    )}
                  </div>
                </div>
              ))}
              {requests.length === 0 && <p>No requests yet.</p>}
            </div>
          </div>
        )}

        {/* Products */}
        {activeTab === 'products' && (
          <div>
            <h2 className="text-lg font-bold mb-4">Available Products</h2>
            <div className="space-y-4">
              {products.map((p) => (
                <div key={p.id} className="border p-4 rounded shadow flex justify-between">
                  <div>
                    <h3 className="font-bold">{p.name}</h3>
                    <p>{p.category}</p>
                    <p>Price: {p.price}</p>
                  </div>
                  <button className="bg-blue-200 px-2 py-1 rounded" onClick={() => {
                    setRequestForm({ product_id: p.id.toString(), quantity: '', note: '' });
                    setEditingRequest(null);
                    setShowRequestModal(true);
                  }}>üìã Request</button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Deliveries */}
        {activeTab === 'deliveries' && (
          <div>
            <h2 className="text-lg font-bold mb-4">My Deliveries</h2>
            <div className="space-y-4">
              {deliveries.map((d) => (
                <div key={d.id} className="border p-4 rounded shadow">
                  <h3 className="font-bold">{d.product_name}</h3>
                  <p>Qty: {d.quantity}</p>
                  <p>Confirmed: {new Date(d.confirmation_date).toLocaleString()}</p>
                  <p>Accepted At: {d.accepted_role}</p>
                  <p>By Admin: {d.accepted_admin_name}</p>
                </div>
              ))}
              {deliveries.length === 0 && <p>No deliveries confirmed yet.</p>}
            </div>
          </div>
        )}
      </div>

      {/* Request Modal */}
      {showRequestModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white p-6 rounded shadow w-96">
            <h3 className="text-lg font-bold mb-4">{editingRequest ? 'Edit Request' : 'New Request'}</h3>
            <form onSubmit={handleRequestSubmit} className="space-y-3">
              <div>
                <label className="block">Product</label>
                <select
                  value={requestForm.product_id}
                  onChange={(e) => setRequestForm({ ...requestForm, product_id: e.target.value })}
                  required
                  disabled={editingRequest}
                  className="border w-full p-2 rounded"
                >
                  <option value="">Select</option>
                  {products.map((p) => (
                    <option key={p.id} value={p.id}>{p.name} - {p.category}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block">Quantity</label>
                <input
                  type="number"
                  min="1"
                  value={requestForm.quantity}
                  onChange={(e) => setRequestForm({ ...requestForm, quantity: e.target.value })}
                  required
                  className="border w-full p-2 rounded"
                />
              </div>
              <div>
                <label className="block">Note</label>
                <textarea
                  value={requestForm.note}
                  onChange={(e) => setRequestForm({ ...requestForm, note: e.target.value })}
                  rows="3"
                  className="border w-full p-2 rounded"
                />
              </div>
              <div className="flex justify-end space-x-2">
                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                  {editingRequest ? 'Update' : 'Create'}
                </button>
                <button type="button" className="bg-gray-300 px-4 py-2 rounded" onClick={() => {
                  setShowRequestModal(false);
                  setEditingRequest(null);
                  setRequestForm({ product_id: '', quantity: '', note: '' });
                }}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Status Modal */}
      {showStatusModal && selectedRequest && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white p-6 rounded shadow w-96">
            <h3 className="text-lg font-bold mb-4">Request Status</h3>
            <p><b>Product:</b> {selectedRequest.product_name}</p>
            <p><b>Quantity:</b> {selectedRequest.quantity}</p>
            <p><b>Status:</b> {getStatusBadge(selectedRequest.status)}</p>
            <div className="mt-4 text-right">
              <button className="bg-gray-300 px-4 py-2 rounded" onClick={() => { setShowStatusModal(false); setSelectedRequest(null); }}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FarmerDashboard;
