// components/AdminManagement.js
import React, { useState, useEffect } from 'react';

const AdminManagement = () => {
        const [admins, setAdmins] = useState([]);
        const [showForm, setShowForm] = useState(false);
        const [formData, setFormData] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            regionName: ''
        });
        const [errors, setErrors] = useState({});

        useEffect(() => {
            const savedAdmins = JSON.parse(localStorage.getItem('regionalAdmins') || '[]');
            setAdmins(savedAdmins);
        }, []);

        const validateForm = () => {
            const newErrors = {};

            if (!formData.fullName.trim()) newErrors.fullName = 'Full name is required';
            if (!formData.phoneNumber.trim()) newErrors.phoneNumber = 'Phone number is required';
            if (!formData.password) newErrors.password = 'Password is required';
            if (formData.password !== formData.confirmPassword) {
                newErrors.confirmPassword = 'Passwords do not match';
            }
            if (!formData.regionName.trim()) newErrors.regionName = 'Region name is required';

            // Check if phone number already exists
            const phoneExists = admins.some(admin => admin.phoneNumber === formData.phoneNumber);
            if (phoneExists) newErrors.phoneNumber = 'Phone number already registered';

            // Check if region name is unique
            const regionExists = admins.some(admin => admin.regionName === formData.regionName);
            if (regionExists) newErrors.regionName = 'Region name must be unique';

            setErrors(newErrors);
            return Object.keys(newErrors).length === 0;
        };

        const handleSubmit = (e) => {
            e.preventDefault();

            if (!validateForm()) return;

            const newAdmin = {
                id: Date.now().toString(),
                fullName: formData.fullName,
                phoneNumber: formData.phoneNumber,
                password: formData.password, // In real app, hash this password
                regionName: formData.regionName,
                role: 'Regional',
                createdAt: new Date().toISOString()
            };

            const updatedAdmins = [...admins, newAdmin];
            setAdmins(updatedAdmins);
            localStorage.setItem('regionalAdmins', JSON.stringify(updatedAdmins));

            setFormData({
                fullName: '',
                phoneNumber: '',
                password: '',
                confirmPassword: '',
                regionName: ''
            });
            setShowForm(false);
            setErrors({});
        };

        const handleDelete = (adminId) => {
            const updatedAdmins = admins.filter(admin => admin.id !== adminId);
            setAdmins(updatedAdmins);
            localStorage.setItem('regionalAdmins', JSON.stringify(updatedAdmins));
        };

        return ( <
                div >
                <
                div className = "flex justify-between items-center mb-6" >
                <
                h2 className = "text-2xl font-bold text-gray-800" > Regional Admin Management < /h2> <
                button onClick = {
                    () => setShowForm(true) }
                className = "bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700" >
                Register Admin <
                /button> <
                /div>

                {
                    showForm && ( <
                            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4" >
                            <
                            div className = "bg-white p-6 rounded-lg w-96" >
                            <
                            h3 className = "text-lg font-bold mb-4" > Register Regional Admin < /h3> <
                            form onSubmit = { handleSubmit }
                            className = "space-y-3" >
                            <
                            div >
                            <
                            input type = "text"
                            placeholder = "Full Name"
                            value = { formData.fullName }
                            onChange = {
                                (e) => setFormData({...formData, fullName: e.target.value }) }
                            className = "w-full p-2 border border-gray-300 rounded" /
                            > {
                                errors.fullName && < p className = "text-red-500 text-sm" > { errors.fullName } < /p>} <
                                /div> <
                                div >
                                <
                                input
                                type = "tel"
                                placeholder = "Phone Number"
                                value = { formData.phoneNumber }
                                onChange = {
                                    (e) => setFormData({...formData, phoneNumber: e.target.value }) }
                                className = "w-full p-2 border border-gray-300 rounded" /
                                > {
                                    errors.phoneNumber && < p className = "text-red-500 text-sm" > { errors.phoneNumber } < /p>} <
                                    /div> <
                                    div >
                                    <
                                    input
                                    type = "password"
                                    placeholder = "Password"
                                    value = { formData.password }
                                    onChange = {
                                        (e) => setFormData({...formData, password: e.target.value }) }
                                    className = "w-full p-2 border border-gray-300 rounded" /
                                    > {
                                        errors.password && < p className = "text-red-500 text-sm" > { errors.password } < /p>} <
                                        /div> <
                                        div >
                                        <
                                        input
                                        type = "password"
                                        placeholder = "Confirm Password"
                                        value = { formData.confirmPassword }
                                        onChange = {
                                            (e) => setFormData({...formData, confirmPassword: e.target.value }) }
                                        className = "w-full p-2 border border-gray-300 rounded" /
                                        > {
                                            errors.confirmPassword && < p className = "text-red-500 text-sm" > { errors.confirmPassword } < /p>} <
                                            /div> <
                                            div >
                                            <
                                            input
                                            type = "text"
                                            placeholder = "Region Name"
                                            value = { formData.regionName }
                                            onChange = {
                                                (e) => setFormData({...formData, regionName: e.target.value }) }
                                            className = "w-full p-2 border border-gray-300 rounded" /
                                            > {
                                                errors.regionName && < p className = "text-red-500 text-sm" > { errors.regionName } < /p>} <
                                                /div> <
                                                div className = "flex space-x-2" >
                                                <
                                                button
                                                type = "submit"
                                                className = "bg-blue-600 text-white px-4 py-2 rounded" >
                                                Register <
                                                /button> <
                                                button
                                                type = "button"
                                                onClick = {
                                                    () => setShowForm(false) }
                                                className = "bg-gray-500 text-white px-4 py-2 rounded" >
                                                Cancel <
                                                /button> <
                                                /div> <
                                                /form> <
                                                /div> <
                                                /div>
                                            )
                                        }

                                        <
                                        div className = "grid gap-4" > {
                                            admins.map((admin) => ( <
                                                div key = { admin.id }
                                                className = "border p-4 rounded-lg" >
                                                <
                                                h3 className = "font-bold text-lg" > { admin.fullName } < /h3> <
                                                p className = "text-gray-600" > 📞{ admin.phoneNumber } < /p> <
                                                p className = "text-blue-600" > 🏛️{ admin.regionName } < /p> <
                                                p className = "text-sm text-gray-500" >
                                                Registered: { new Date(admin.createdAt).toLocaleDateString() } <
                                                /p> <
                                                button onClick = {
                                                    () => handleDelete(admin.id) }
                                                className = "bg-red-500 text-white px-3 py-1 rounded text-sm mt-2" >
                                                Delete <
                                                /button> <
                                                /div>
                                            ))
                                        } <
                                        /div> <
                                        /div>
                                    );
                                };

                                export default AdminManagement;
