import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, collection, query, onSnapshot } from 'firebase/firestore';

// Import all components
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Modal from './components/Modal';
import LoginForm from './components/LoginForm';
import DashboardPage from './pages/DashboardPage';
import ReportsPage from './pages/ReportsPage';
import NewRequestForm from './pages/NewRequestForm';
import RequestApprovalForm from './pages/RequestApprovalForm';

// The following global variables are provided by the Canvas environment.
// We must always use them for Firebase setup.
// eslint-disable-next-line no-undef
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
// eslint-disable-next-line no-undef
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
// eslint-disable-next-line no-undef
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

// ----------------------
// Firebase Initialization
// ----------------------
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// ----------------------
// Main App Component
// ----------------------
export default function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [loading, setLoading] = useState(true);
    const [requests, setRequests] = useState([]);
    const [locations, setLocations] = useState({ regions: [], zones: [], woredas: [] });
    const [currentPage, setCurrentPage] = useState('dashboard');
    const [showModal, setShowModal] = useState(false);
    const [modalContent, setModalContent] = useState('');
    const [selectedRequest, setSelectedRequest] = useState(null);

    // Get the current user's role from their details in Firestore
    const fetchUserRole = async(uid) => {
        try {
            const userRef = doc(db, 'artifacts', appId, 'users', uid);
            const userSnap = await getDoc(userRef);
            if (userSnap.exists()) {
                const userData = userSnap.data();
                setCurrentUser(prev => ({...prev, ...userData }));
            }
        } catch (error) {
            console.error('Error fetching user role:', error);
        }
    };

    // Firebase authentication and data fetching
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async(user) => {
            if (user) {
                setCurrentUser(user);
                await fetchUserRole(user.uid);
                setAuthReady(true);
            } else {
                try {
                    if (initialAuthToken) {
                        await signInWithCustomToken(auth, initialAuthToken);
                    } else {
                        await signInAnonymously(auth);
                    }
                } catch (error) {
                    console.error('Authentication Error:', error);
                }
            }
        });
        return () => unsubscribe();
    }, []);

    // Fetch locations from Firestore
    useEffect(() => {
        if (!authReady) return;

        const locationsRef = collection(db, 'artifacts', appId, 'public', 'data', 'locations');
        const unsubscribeLocations = onSnapshot(locationsRef, (snapshot) => {
            const fetchedLocations = { regions: [], zones: [], woredas: [] };
            snapshot.docs.forEach(doc => {
                const data = doc.data();
                if (data.type === 'region') fetchedLocations.regions.push(data);
                if (data.type === 'zone') fetchedLocations.zones.push(data);
                if (data.type === 'woreda') fetchedLocations.woredas.push(data);
            });
            setLocations(fetchedLocations);
            setLoading(false);
        });

        return () => unsubscribeLocations();
    }, [authReady]);

    // Fetch requests from Firestore
    useEffect(() => {
        if (!authReady || !currentUser) return;

        const requestsRef = collection(db, 'artifacts', appId, 'public', 'data', 'requests');
        const q = query(requestsRef);

        const unsubscribeRequests = onSnapshot(q, (snapshot) => {
            const fetchedRequests = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setRequests(fetchedRequests);
        });

        return () => unsubscribeRequests();
    }, [authReady, currentUser]);

    // Function to handle showing the modal
    const showModalMessage = (message) => {
        setModalContent(message);
        setShowModal(true);
    };

    // Handle user login and role assignment
    const handleLogin = async(userCredentials) => {
        let userId;
        if (auth.currentUser) {
            userId = auth.currentUser.uid;
        } else {
            userId = crypto.randomUUID();
        }

        const userRef = doc(db, 'artifacts', appId, 'users', userId);

        const userData = {
            role: userCredentials.role,
            locationLevel: userCredentials.locationLevel,
            locationId: userCredentials.locationId,
        };

        try {
            await setDoc(userRef, userData, { merge: true });
            setCurrentUser(prev => ({...prev, ...userData }));
            showModalMessage('Login successful!');
        } catch (error) {
            console.error('Error setting user data:', error);
            showModalMessage('Failed to set user data. Please try again.');
        }
    };

    const handleViewRequest = (request) => {
        setSelectedRequest(request);
        setCurrentPage('view-request');
    };

    const getPendingRequests = () => {
        return requests.filter(req => req.status === 'Pending');
    };

    const getApprovedRequests = () => {
        return requests.filter(req => req.status === 'Approved');
    };

    const getRejectedRequests = () => {
        return requests.filter(req => req.status === 'Rejected');
    };

    if (loading || !authReady) {
        return <div className = "flex items-center justify-center min-h-screen" > Loading... < /div>;
    }

    if (!currentUser || !currentUser.role) {
        return <LoginForm onLogin = { handleLogin }
        locations = { locations }
        showModal = { showModalMessage }
        />;
    }

    return ( <
            div className = "flex min-h-screen bg-gray-100 font-sans antialiased" >
            <
            Sidebar currentPage = { currentPage }
            setCurrentPage = { setCurrentPage }
            /> <
            div className = "flex-1 flex flex-col" >
            <
            Header user = { currentUser }
            /> <
            main className = "flex-1 p-8" > {
                currentPage === 'dashboard' && < DashboardPage user = { currentUser }
                requests = { requests }
                onViewRequest = { handleViewRequest }
                />} {
                    currentPage === 'pending-requests' && < DashboardPage user = { currentUser }
                    requests = { getPendingRequests() }
                    onViewRequest = { handleViewRequest }
                    />} {
                        currentPage === 'approved-requests' && < DashboardPage user = { currentUser }
                        requests = { getApprovedRequests() }
                        onViewRequest = { handleViewRequest }
                        />} {
                            currentPage === 'rejected-requests' && < DashboardPage user = { currentUser }
                            requests = { getRejectedRequests() }
                            onViewRequest = { handleViewRequest }
                            />} {
                                currentPage === 'reports' && < ReportsPage user = { currentUser }
                                />} {
                                    currentPage === 'new-request' && < NewRequestForm user = { currentUser }
                                    showModal = { showModalMessage }
                                    />} {
                                        currentPage === 'view-request' && < RequestApprovalForm user = { currentUser }
                                        request = { selectedRequest }
                                        showModal = { showModalMessage }
                                        />} <
                                        /main> <
                                        /div> <
                                        Modal show = { showModal }
                                        onClose = {
                                            () => setShowModal(false) }
                                        title = "Notification"
                                        message = { modalContent }
                                        /> <
                                        /div>
                                    );
                                }
