                >
                <
                /div>

                <
                div >
                <
                label className = "block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1" >
                Note(Optional) <
                /label> <
                textarea value = { requestForm.note }
                onChange = {
                    (e) => setRequestForm({...requestForm, note: e.target.value }) }
                rows = "3"
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border border-gray-300 text-gray-900'}` }
                /> <
                /div> <
                /div>

                <
                div className = "flex justify-end space-x-3 mt-6" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowRequestModal(false);
                        setEditingRequest(null);
                        setRequestForm({ product_id: '', quantity: '', note: '' });
                    }
                }
                className = { `px-4 py-2 rounded ${darkMode ? 'bg-gray-600 hover:bg-gray-700 text-white' : 'bg-gray-200 hover:bg-gray-300 text-gray-800'}` } >
                Cancel <
                /button> <
                button type = "submit"
                className = "px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded" >
                { editingRequest ? 'Update Request' : 'Submit Request' } <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        }

        { /* Status Detail Modal */ } {
            showStatusModal && selectedRequest && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = { `w-full max-w-2xl rounded-lg shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'} p-6 max-h-[90vh] overflow-y-auto` } >
                <
                div className = "flex justify-between items-center mb-4" >
                <
                h3 className = "text-lg font-semibold text-gray-900 dark:text-white" >
                Request Status Details <
                /h3> <
                button onClick = {
                    () => setShowStatusModal(false) }
                className = "text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200" >
                âœ•
                <
                /button> <
                /div>

                <
                div className = "space-y-4" >
                <
                div className = "bg-gray-50 dark:bg-gray-700 p-4 rounded-lg" >
                <
                h4 className = "font-semibold text-gray-900 dark:text-white mb-2" > Request Information < /h4> <
                div className = "grid grid-cols-2 gap-2 text-sm" >
                <
                div >
                <
                span className = "font-medium" > Product: < /span> {selectedRequest.product_name} <
                /div> <
                div >
                <
                span className = "font-medium" > Quantity: < /span> {selectedRequest.quantity} {selectedRequest.unit} <
                /div> <
                div >
                <
                span className = "font-medium" > Category: < /span> {selectedRequest.category} <
                /div> {
                    selectedRequest.sub_category && ( <
                        div >
                        <
                        span className = "font-medium" > Sub - category: < /span> {selectedRequest.sub_category} <
                        /div>
                    )
                } <
                div >
                <
                span className = "font-medium" > Requested: < /span> {new Date(selectedRequest.created_at).toLocaleString()} <
                /div> <
                /div> <
                /div>

                <
                div className = "grid gap-4" > { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) } { getLevelStatus('federal', selectedRequest) } <
                /div> <
                /div> <
                /div> <
                /div>
            )
        }

        { /* Delivery Confirmation Modal */ } {
            showDeliveryModal && confirmingDelivery && ( <
                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                <
                div className = { `w-full max-w-md rounded-lg shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'} p-6` } >
                <
                h3 className = "text-lg font-semibold mb-4 text-gray-900 dark:text-white" > { t.confirmDelivery } <
                /h3> <
                form onSubmit = { submitDeliveryConfirmation } >
                <
                div className = "mb-4" >
                <
                p className = "text-sm text-gray-600 dark:text-gray-400 mb-2" >
                You are confirming delivery
                for: < strong > { confirmingDelivery.product_name } < /strong> <
                /p> <
                p className = "text-sm text-gray-600 dark:text-gray-400" >
                Quantity: < strong > { confirmingDelivery.quantity } { confirmingDelivery.unit } < /strong> <
                /p> <
                /div>

                <
                div className = "mb-4" >
                <
                label className = "block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1" >
                Delivery Note(Optional) <
                /label> <
                textarea value = { deliveryNote }
                onChange = {
                    (e) => setDeliveryNote(e.target.value) }
                rows = "3"
                className = { `w-full p-2 rounded ${darkMode ? 'bg-gray-700 text-white border-gray-600' : 'bg-white border border-gray-300 text-gray-900'}` }
                placeholder = "Add any notes about the delivery..." /
                >
                <
                /div>

                <
                div className = "flex justify-end space-x-3" >
                <
                button type = "button"
                onClick = {
                    () => {
                        setShowDeliveryModal(false);
                        setConfirmingDelivery(null);
                        setDeliveryNote('');
                    }
                }
                className = { `px-4 py-2 rounded ${darkMode ? 'bg-gray-600 hover:bg-gray-700 text-white' : 'bg-gray-200 hover:bg-gray-300 text-gray-800'}` } >
                Cancel <
                /button> <
                button type = "submit"
                className = "px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded" >
                Confirm Delivery <
                /button> <
                /div> <
                /form> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default FarmerDashboard;
