import React from 'react';
import { pageNames, adminRoles } from '../constants';

const navItems = [
    { name: pageNames.DASHBOARD, icon: '🏠' },
    { name: pageNames.REQUESTS, icon: '📝' },
    { name: pageNames.APPROVALS, icon: '✅' },
    { name: pageNames.MASTER_DATA, icon: '📊' },
    { name: pageNames.REPORTS, icon: '📈' },
];

const Sidebar = ({ userRole, currentPage, onNavigate }) => {
    // Logic to determine which items to show based on the user's role
    const filteredNavItems = navItems.filter(item => {
        switch (userRole) {
            case adminRoles.FEDERAL:
                return true; // Federal Admin can see all
            case adminRoles.REGIONAL:
                return true; // Regional can see all
            case adminRoles.ZONE:
                return true; // Zone can see all
            case adminRoles.WOREDA:
                return true; // Woreda can see all
            case adminRoles.KEBELE:
                return true; // Kebele can see all
            case adminRoles.FARMER:
                return item.name === pageNames.DASHBOARD || item.name === pageNames.REQUESTS;
            default:
                return false;
        }
    });

    return ( <
        aside className = "w-64 bg-white shadow-lg h-screen fixed" >
        <
        div className = "p-6" >
        <
        h1 className = "text-xl font-bold text-gray-800" > Admin Panel < /h1> < /
        div > <
        nav className = "mt-8" > {
            filteredNavItems.map(item => ( <
                button key = { item.name }
                onClick = {
                    () => onNavigate(item.name)
                }
                className = { `w-full text-left px-6 py-3 transition-colors duration-200 ${
              currentPage === item.name
                ? 'bg-indigo-600 text-white font-semibold'
                : 'text-gray-600 hover:bg-gray-100'
            }` } >
                <
                span className = "mr-3" > { item.icon } < /span> { item.name } < /
                button >
            ))
        } <
        /nav> < /
        aside >
    );
};

export default Sidebar;
