    // ----------------------
    // Login Page Component
    // ----------------------
    const LoginPage = ({ onLogin, users }) => {
        const [phoneNumber, setPhoneNumber] = useState('');
        const [password, setPassword] = useState('');
        const [showModal, setShowModal] = useState(false);
        const [modalMessage, setModalMessage] = useState('');

        const handleSubmit = (e) => {
            e.preventDefault();
            const user = users.find(
                (u) => u.phoneNumber === phoneNumber && u.password === password
            );
            if (user) {
                onLogin(user);
            } else {
                setModalMessage('Invalid phone number or password.');
                setShowModal(true);
            }
        };
        return ( <
            div className = "flex items-center justify-center min-h-screen bg-gray-200 dark:bg-gray-900" >
