            /> < /
            div >
        );
    };

    // ----------------------
    // Dashboard Overview Component
    // ----------------------
    const DashboardOverview = ({ requests, currentUser }) => {
        const stats = {
            total: requests.length,
            approved: requests.filter(r => r.status === 'Approved').length,
            pending: requests.filter(r => r.status === 'Pending').length,
            rejected: requests.filter(r => r.status === 'Rejected').length
        };

        const StatCard = ({ title, value, color }) => ( <
            div className = { `p-6 rounded-xl shadow-md bg-white dark:bg-gray-800 border-l-4 ${color}` } >
            <
            h3 className = "text-md font-medium text-gray-500 dark:text-gray-300" > { title } < /h3> <
            p className = "mt-1 text-3xl font-semibold text-gray-900 dark:text-white" > { value } < /p> < /
            div >
        );

        return ( <
            div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6" >
            <
            StatCard title = "Total Requests"
            value = { stats.total }
            color = "border-blue-500" / >
            <
            StatCard title = "Approved Requests"
            value = { stats.approved }
            color = "border-green-500" / >
            <
            StatCard title = "Pending Requests"
            value = { stats.pending }
            color = "border-yellow-500" / >
            <
            StatCard title = "Rejected Requests"
            value = { stats.rejected }
            color = "border-red-500" / >
            <
            /div>
        );
    };

    // ----------------------
    // Requests Table Component
    // ----------------------
    const RequestsTable = ({ requests, currentUser, setSelectedRequest }) => {
        return ( <
            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
            <
            h2 className = "text-2xl font-bold mb-4" > All Product Requests < /h2> <
            div className = "overflow-x-auto" >
            <
            table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
            <
            thead className = "bg-gray-50 dark:bg-gray-700" >
            <
            tr >
            <
            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > ID < /th> <
            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Requester < /th> <
            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product < /th> <
            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status < /th> <
            th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Actions < /th> < /
            tr > <
            /thead> <
            tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                requests.map((req) => ( <
                    tr key = { req.id } >
                    <
                    td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.id } < /td> <
                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.requesterName } < /td> <
                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.productName } < /td> <
                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                    td className = "px-6 py-4 whitespace-nowrap" >
                    <
                    span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                        req.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                        req.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                        'bg-red-100 text-red-800'
                                    }` } > { req.status } <
                    /span> < /
                    td > <
                    td className = "px-6 py-4 whitespace-nowrap text-right text-sm font-medium" >
                    <
                    button onClick = {
                        () => setSelectedRequest(req)
                    }
                    className = "text-blue-600 hover:text-blue-900" >
                    View <
                    /button> < /
                    td > <
                    /tr>
                ))
            } <
            /tbody> < /
            table > <
            /div> < /
            div >
        );
    };

    // ----------------------
    // Request Approval Form
    // ----------------------
    const RequestApprovalForm = ({ request, onDecision, onClose }) => {
        const [feedback, setFeedback] = useState('');
        if (!request) return null;
        return ( <
            div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
            <
            h2 className = "text-2xl font-bold mb-4" > Request Details < /h2> <
            p > < span className = "font-semibold" > Request ID: < /span> {request.id}</p >
            <
            p > < span className = "font-semibold" > Requester: < /span> {request.requesterName}</p >
            <
            p > < span className = "font-semibold" > Product: < /span> {request.productName}</p >
            <
            p > < span className = "font-semibold" > Amount: < /span> {request.amount}</p >
            <
            p > < span className = "font-semibold" > Purpose: < /span> {request.productPurpose}</p >
            <
            p > < span className = "font-semibold" > Status: < /span> {request.status}</p >
            <
            div className = "mt-4" >
            <
            label htmlFor = "feedback"
            className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Feedback < /label> <
            textarea id = "feedback"
            rows = "3"
            value = { feedback }
            onChange = {
                (e) => setFeedback(e.target.value)
            }
            className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" >
            <
            /textarea> < /
            div > <
            div className = "mt-4 flex justify-end space-x-2" >
            <
            button onClick = {
                () => onDecision(request.id, 'Approve', feedback)
            }
            className = "px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700" >
            Approve <
            /button> <
            button onClick = {
                () => onDecision(request.id, 'Reject', feedback)
            }
            className = "px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700" >
            Reject <
            /button> <
            button onClick = { onClose }
            className = "px-4 py-2 bg-gray-300 text-gray-800 rounded-lg hover:bg-gray-400 dark:bg-gray-600 dark:text-white dark:hover:bg-gray-500" > Close < /button> < /
            div > <
            /div>
        );
    };

    // ----------------------
    // Master Data Component
    // ----------------------
    const MasterData = ({ currentUser, users, products, onRegister, onDeleteUser, onAddProduct, onUpdateProduct, onDeleteProduct }) => {
        const [showDeleteUserConfirm, setShowDeleteUserConfirm] = useState(false);
        const [userToDelete, setUserToDelete] = useState(null);
        const [showDeleteProductConfirm, setShowDeleteProductConfirm] = useState(false);
        const [productToDelete, setProductToDelete] = useState(null);

        // Determine which roles this user can manage
        const getRolesToManage = (role) => {
            switch (role) {
                case 'Federal':
                    return ['Region'];
                case 'Region':
                    return ['Zone'];
                case 'Zone':
                    return ['Woreda'];
                case 'Woreda':
                    return ['Kebele'];
                case 'Kebele':
                    return ['Farmer'];
                default:
                    return [];
            }
        };
        const rolesToManage = getRolesToManage(currentUser.role);
        const canManageProducts = ['Federal', 'Region', 'Zone', 'Woreda'].includes(currentUser.role);

        const getUsersByRole = (role) => {
            // Filter users based on their role and the current user's location
            const filteredUsers = users.filter(user => user.role === role);
            return filteredUsers.filter(user => {
                switch (currentUser.role) {
                    case 'Federal':
                        return true;
                    case 'Region':
                        return user.regionName === currentUser.regionName;
                    case 'Zone':
                        return user.zoneName === currentUser.zoneName;
                    case 'Woreda':
                        return user.woredaName === currentUser.woredaName;
                    case 'Kebele':
                        return user.kebeleName === currentUser.kebeleName;
                    default:
                        return false;
                }
            });
        };

        // Filter products based on user's location
        const getFilteredProducts = (user, allProducts) => {
            switch (user.role) {
                case 'Federal':
                    return allProducts;
                case 'Region':
                    return allProducts.filter(p => p.regionName === user.regionName || !p.regionName);
                case 'Zone':
                    return allProducts.filter(p => p.zoneName === user.zoneName || !p.zoneName);
                case 'Woreda':
                    return allProducts.filter(p => p.woredaName === user.woredaName || !p.woredaName);
                default:
                    return [];
            }
        };

        const handleConfirmDeleteUser = () => {
            onDeleteUser(userToDelete.id, userToDelete.role);
            setShowDeleteUserConfirm(false);
            setUserToDelete(null);
        };

        const handleConfirmDeleteProduct = () => {
            onDeleteProduct(productToDelete.id);
            setShowDeleteProductConfirm(false);
            setProductToDelete(null);
        };

        return ( <
                div className = "space-y-8" >
                <
                h2 className = "text-3xl font-bold" > Master Data Management < /h2> <
                div className = "grid grid-cols-1 md:grid-cols-2 gap-8" > { /* User Management Section */ } <
                div className = "space-y-4" >
                <
                h3 className = "text-2xl font-bold" > User Management < /h3> {
                rolesToManage.map(role => ( <
                    div key = { role }
                    className = "bg-white dark:bg-gray-800 p-4 rounded-xl shadow-md" >
                    <
                    h4 className = "text-xl font-semibold mb-2" > Register { role }
                    Admin < /h4> <
                    RegistrationForm role = { role }
                    onRegister = { onRegister }
                    /> <
                    ul className = "mt-4 space-y-2" > {
                        getUsersByRole(role).map(user => ( <
                            li key = { user.id }
                            className = "flex justify-between items-center bg-gray-100 dark:bg-gray-700 p-2 rounded-lg" >
                            <
                            span > { user.fullName }(ID: { user.id }) < /span> <
                            button onClick = {
                                () => {
                                    setUserToDelete(user);
                                    setShowDeleteUserConfirm(true);
                                }
                            }
                            className = "text-red-500 hover:text-red-700" > Delete < /button> < /
                            li >
                        ))
                    } <
                    /ul> < /
                    div >
                ))
            } <
            /div>

        { /* Product Management Section */ } {
            canManageProducts && ( <
                div className = "space-y-4" >
                <
                h3 className = "text-2xl font-bold" > Product Management < /h3> <
                ProductForm onSubmit = { onAddProduct }
                /> <
                ul className = "mt-4 space-y-2" > {
                    getFilteredProducts(currentUser, products).map(product => ( <
                        li key = { product.id }
                        className = "flex justify-between items-center bg-white dark:bg-gray-800 p-4 rounded-xl shadow-md" >
                        <
                        div >
                        <
                        div className = "font-bold" > { product.name } < /div> <
                        div className = "text-sm text-gray-600 dark:text-gray-400" > { product.description } < /div> < /
                        div > <
                        div className = "flex space-x-2" >
                        <
                        button onClick = {
                            () => onUpdateProduct({...product, name: product.name + ' (Edited)' })
                        }
                        className = "text-blue-500 hover:text-blue-700" > Edit < /button> <
                        button onClick = {
                            () => {
                                setProductToDelete(product);
                                setShowDeleteProductConfirm(true);
                            }
                        }
                        className = "text-red-500 hover:text-red-700" > Delete < /button> < /
                        div > <
                        /li>
                    ))
                } <
                /ul> < /
                div >
            )
        } <
        /div> <
        ConfirmationModal show = { showDeleteUserConfirm }
        onClose = {
            () => setShowDeleteUserConfirm(false)
        }
        onConfirm = { handleConfirmDeleteUser }
        title = "Confirm User Deletion"
        message = { `Are you sure you want to delete the account for ${userToDelete?.fullName}? This action cannot be undone.` }
        /> <
        ConfirmationModal show = { showDeleteProductConfirm }
        onClose = {
            () => setShowDeleteProductConfirm(false)
        }
        onConfirm = { handleConfirmDeleteProduct }
        title = "Confirm Product Deletion"
        message = { `Are you sure you want to delete the product "${productToDelete?.name}"? This action cannot be undone.` }
        /> < /
        div >
    );
};

// ----------------------
// Farmer Dashboard Component
// ----------------------
const FarmerDashboard = ({ currentUser, products, requests, onRequest }) => {
        const { register, handleSubmit, formState: { errors } } = useForm();
        const [showForm, setShowForm] = useState(false);

        const onSubmit = (data) => {
            onRequest({
                productName: data.productName,
                productPurpose: data.productPurpose,
                amount: data.amount,
                regionName: currentUser.regionName,
                zoneName: currentUser.zoneName,
                woredaName: currentUser.woredaName,
                kebeleName: currentUser.kebeleName,
            });
            setShowForm(false);
        };

        return ( <
                div className = "space-y-8" >
                <
                h1 className = "text-3xl font-bold" > Welcome, { currentUser.fullName } < /h1> <
                div className = "grid grid-cols-1 md:grid-cols-2 gap-8" > { /* Product Request Form */ } <
                div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                <
                div className = "flex justify-between items-center mb-4" >
                <
                h2 className = "text-2xl font-bold" > New Product Request < /h2> <
                button onClick = {
                    () => setShowForm(!showForm)
                }
                className = "px-4 py-2 bg-green-500 text-white font-medium rounded-lg hover:bg-green-600 transition" > { showForm ? 'Hide Form' : 'Start Request' } <
                /button> < /
                div > {
                    showForm && ( <
                        form onSubmit = { handleSubmit(onSubmit) }
                        className = "space-y-4" >
                        <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Select Product < /label> <
                        select className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" {...register("productName", { required: "Product is required" }) } >
                        <
                        option value = "" > --Select a product-- < /option> {
                        products.map(p => ( <
                            option key = { p.id }
                            value = { p.name } > { p.name } < /option>
                        ))
                    } <
                    /select> {
                    errors.productName && < p className = "text-red-500 text-xs mt-1" > { errors.productName.message } < /p>} < /
                    div > <
                    div >
                    <
                    label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Purpose < /label> <
                    input
                    type = "text"
                    className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" {...register("productPurpose", { required: "Purpose is required" }) }
                    /> {
                    errors.productPurpose && < p className = "text-red-500 text-xs mt-1" > { errors.productPurpose.message } < /p>} < /
                    div > <
                    div >
                    <
                    label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" > Amount < /label> <
                    input
                    type = "number"
                    className = "mt-1 w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-gray-100" {...register("amount", { required: "Amount is required", min: 1 }) }
                    /> {
                    errors.amount && < p className = "text-red-500 text-xs mt-1" > { errors.amount.message } < /p>} < /
                    div > <
                    button
                    type = "submit"
                    className = "w-full p-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition shadow-md" >
                    Submit Request <
                    /button> < /
                    form >
                )
            } <
            /div>

        { /* My Request Status */ } <
        div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
            <
            h2 className = "text-2xl font-bold mb-4" > My Request Status < /h2> {
        requests.length > 0 ? ( <
            ul className = "divide-y divide-gray-200 dark:divide-gray-700" > {
                requests.map(request => ( <
                    li key = { request.id }
                    className = "py-4 flex justify-between items-center" >
                    <
                    div >
                    <
                    div className = "text-lg font-medium text-gray-900 dark:text-white" > { request.productName } < /div> <
                    div className = "text-sm text-gray-500 dark:text-gray-400" > Status: { request.status } < /div> < /
                    div > <
                    span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                        request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                        request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                        'bg-red-100 text-red-800'
                                    }` } > { request.status } <
                    /span> < /
                    li >
                ))
            } <
            /ul>
        ) : ( <
            p className = "text-gray-500" > You have not submitted any requests yet. < /p>
        )
    } <
    /div> < /
    div > <
    /div>
);
};

// ----------------------
// Reports Component
// ----------------------
const Reports = ({ requests, currentUser }) => {
    const [reportType, setReportType] = useState('summary');

    const requestCounts = requests.reduce((acc, req) => {
        acc[req.productName] = (acc[req.productName] || 0) + req.amount;
        return acc;
    }, {});

    const statusCounts = requests.reduce((acc, req) => {
        acc[req.status] = (acc[req.status] || 0) + req.amount;
        return acc;
    }, {});

    return ( <
        div className = "space-y-6" >
        <
        h2 className = "text-3xl font-bold" > Reports < /h2> <
        div className = "flex space-x-4 mb-4" >
        <
        button onClick = {
            () => setReportType('summary')
        }
        className = { `px-4 py-2 rounded-lg ${reportType === 'summary' ? 'bg-blue-600 text-white' : 'bg-gray-200 dark:bg-gray-700'}` } >
        Summary <
        /button> <
        button onClick = {
            () => setReportType('detailed')
        }
        className = { `px-4 py-2 rounded-lg ${reportType === 'detailed' ? 'bg-blue-600 text-white' : 'bg-gray-200 dark:bg-gray-700'}` } >
        Detailed <
        /button> < /
        div >

        {
            reportType === 'summary' && ( <
                div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                <
                h3 className = "text-2xl font-bold mb-4" > Request Summary < /h3> <
                div className = "grid grid-cols-1 md:grid-cols-2 gap-4" >
                <
                div >
                <
                h4 className = "text-xl font-semibold" > Requests by Product < /h4> <
                ul className = "mt-2" > {
                    Object.entries(requestCounts).map(([product, count]) => ( <
                        li key = { product }
                        className = "flex justify-between items-center" >
                        <
                        span > { product } < /span> <
                        span className = "font-medium" > { count } < /span> < /
                        li >
                    ))
                } <
                /ul> < /
                div > <
                div >
                <
                h4 className = "text-xl font-semibold" > Requests by Status < /h4> <
                ul className = "mt-2" > {
                    Object.entries(statusCounts).map(([status, count]) => ( <
                        li key = { status }
                        className = "flex justify-between items-center" >
                        <
                        span > { status } < /span> <
                        span className = "font-medium" > { count } < /span> < /
                        li >
                    ))
                } <
                /ul> < /
                div > <
                /div> < /
                div >
            )
        }

        {
            reportType === 'detailed' && ( <
                div className = "bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md" >
                <
                h3 className = "text-2xl font-bold mb-4" > Detailed Requests < /h3> <
                div className = "overflow-x-auto" >
                <
                table className = "min-w-full divide-y divide-gray-200 dark:divide-gray-700" >
                <
                thead className = "bg-gray-50 dark:bg-gray-700" >
                <
                tr >
                <
                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > ID < /th> <
                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Requester < /th> <
                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Product < /th> <
                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Amount < /th> <
                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Status < /th> <
                th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" > Location < /th> < /
                tr > <
                /thead> <
                tbody className = "bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700" > {
                    requests.map(req => ( <
                        tr key = { req.id } >
                        <
                        td className = "px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white" > { req.id } < /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.requesterName } < /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.productName } < /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.amount } < /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.status } < /td> <
                        td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400" > { req.regionName }, { req.zoneName }, { req.woredaName }, { req.kebeleName } < /td> < /
                        tr >
                    ))
                } <
                /tbody> < /
                table > <
                /div> < /
                div >
            )
        } <
        /div>
    );
};

// ----------------------
// Registration Form Component
// ----------------------
const RegistrationForm = ({ role, onRegister }) => {
        const { register, handleSubmit, formState: { errors } } = useForm();

        const onSubmit = (data) => {
            onRegister({...data, role });
        };

        return ( <
                form onSubmit = { handleSubmit(onSubmit) }
                className = "space-y-4 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg shadow-inner" >
                <
                input type = "text"
                placeholder = "Full Name" {...register("fullName", { required: "Full name is required" }) }
                className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                >
                {
                    errors.fullName && < p className = "text-red-500 text-xs mt-1" > { errors.fullName.message } < /p>} <
                    input
                    type = "text"
                    placeholder = "Phone Number" {...register("phoneNumber", { required: "Phone number is required" }) }
                    className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                    >
                    {
                        errors.phoneNumber && < p className = "text-red-500 text-xs mt-1" > { errors.phoneNumber.message } < /p>} <
                        input
                        type = "password"
                        placeholder = "Password" {...register("password", { required: "Password is required" }) }
                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                        >
                        {
                            errors.password && < p className = "text-red-500 text-xs mt-1" > { errors.password.message } < /p>} <
                            button
                            type = "submit"
                            className = "w-full p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" >
                            Register { role } <
                            /button> < /
                            form >
                        );
                    };

                    // ----------------------
                    // Product Form Component
                    // ----------------------
                    const ProductForm = ({ onSubmit, defaultValues = {} }) => {
                            const { register, handleSubmit, formState: { errors } } = useForm({ defaultValues });
                            return ( <
                                    form onSubmit = { handleSubmit(onSubmit) }
                                    className = "space-y-4 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg shadow-inner" >
                                    <
                                    h3 className = "text-xl font-bold" > Add / Edit Product < /h3> <
                                    input type = "text"
                                    placeholder = "Product Name" {...register("name", { required: "Product name is required" }) }
                                    className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                    >
                                    {
                                        errors.name && < p className = "text-red-500 text-xs mt-1" > { errors.name.message } < /p>} <
                                        textarea
                                        placeholder = "Description" {...register("description") }
                                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" >
                                        <
                                        /textarea> <
                                        input
                                        type = "text"
                                        placeholder = "Category" {...register("category", { required: "Category is required" }) }
                                        className = "w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100" /
                                        >
                                        {
                                            errors.category && < p className = "text-red-500 text-xs mt-1" > { errors.category.message } < /p>} <
                                            button
                                            type = "submit"
                                            className = "w-full p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700" >
                                            Save Product <
                                            /button> < /
                                            form >
                                        );
                                    };

                                    export default App;
