import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ZoneDashboard = () => {
        const [activeTab, setActiveTab] = useState('overview');
        const [user, setUser] = useState(null);
        const navigate = useNavigate();

        // Woreda Admin Management State
        const [woredaAdmins, setWoredaAdmins] = useState([]);
        const [showWoredaForm, setShowWoredaForm] = useState(false);
        const [editingWoreda, setEditingWoreda] = useState(null);
        const [woredaForm, setWoredaForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            woreda_name: ''
        });
        const [woredaError, setWoredaError] = useState('');

        // Farmer Management State
        const [farmers, setFarmers] = useState([]);
        const [showFarmerForm, setShowFarmerForm] = useState(false);
        const [editingFarmer, setEditingFarmer] = useState(null);
        const [farmerForm, setFarmerForm] = useState({
            fullName: '',
            phoneNumber: '',
            password: '',
            confirmPassword: '',
            woreda_name: '',
            kebele_name: ''
        });
        const [farmerError, setFarmerError] = useState('');

        // Product Management State
        const [products, setProducts] = useState([]);
        const [showProductForm, setShowProductForm] = useState(false);
        const [editingProduct, setEditingProduct] = useState(null);
        const [productForm, setProductForm] = useState({
            name: '',
            category: '',
            amount: '',
            description: '',
            price: ''
        });

        // Request Management State
        const [requests, setRequests] = useState([]);
        const [filteredRequests, setFilteredRequests] = useState([]);
        const [selectedRequest, setSelectedRequest] = useState(null);
        const [showRequestModal, setShowRequestModal] = useState(false);
        const [requestStatus, setRequestStatus] = useState('');
        const [decisionReason, setDecisionReason] = useState('');
        const [statusFilter, setStatusFilter] = useState('all');
        const [woredaFilter, setWoredaFilter] = useState('all');

        // Stats State
        const [stats, setStats] = useState({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            rejectedRequests: 0,
            acceptedRequests: 0,
            totalWoredaAdmins: 0,
            totalFarmers: 0,
            totalProducts: 0
        });

        // Woredas in this Zone
        const [woredas, setWoredas] = useState([]);

        useEffect(() => {
            // Check authentication
            const token = localStorage.getItem('token');
            const userData = JSON.parse(localStorage.getItem('user') || 'null');
            const userType = localStorage.getItem('userType');

            if (!token || userType !== 'Zone' || !userData) {
                navigate('/login');
                return;
            }

            setUser(userData);
            fetchDashboardData();
        }, [navigate]);

        useEffect(() => {
            if (requests.length > 0) {
                filterRequests();
            }
        }, [statusFilter, woredaFilter, requests]);

        const filterRequests = () => {
            let filtered = [...requests];

            // Filter by status
            if (statusFilter !== 'all') {
                filtered = filtered.filter(req => req.status === statusFilter);
            }

            // Filter by woreda
            if (woredaFilter !== 'all') {
                filtered = filtered.filter(req => req.woreda_name === woredaFilter);
            }

            setFilteredRequests(filtered);
        };

        const fetchDashboardData = async() => {
            try {
                const token = localStorage.getItem('token');

                // Fetch woreda admins
                const adminsResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (adminsResponse.ok) {
                    const data = await adminsResponse.json();
                    const woredaAdminsData = data.filter(item => item.type === 'admin' && item.role === 'Woreda');
                    setWoredaAdmins(woredaAdminsData);
                    setStats(prev => ({...prev, totalWoredaAdmins: woredaAdminsData.length }));

                    // Extract unique woredas for filtering
                    const uniqueWoredas = [...new Set(woredaAdminsData.map(a => a.woreda_name))];
                    setWoredas(uniqueWoredas);
                }

                // Fetch farmers
                const farmersResponse = await fetch('http://localhost:5000/api/admins/admins', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (farmersResponse.ok) {
                    const data = await farmersResponse.json();
                    const farmersData = data.filter(item => item.type === 'farmer');
                    setFarmers(farmersData);
                    setStats(prev => ({...prev, totalFarmers: farmersData.length }));
                }

                // Fetch products
                const productsResponse = await fetch('http://localhost:5000/api/admins/getproducts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (productsResponse.ok) {
                    const productsData = await productsResponse.json();
                    setProducts(productsData);
                    setStats(prev => ({...prev, totalProducts: productsData.length }));
                }

                // Fetch requests with detailed status
                const requestsResponse = await fetch('http://localhost:5000/api/admins/requests/status', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (requestsResponse.ok) {
                    const requestsData = await requestsResponse.json();
                    setRequests(requestsData);
                    filterRequests();

                    // Calculate stats from requests data
                    const totalRequests = requestsData.length;
                    const pendingRequests = requestsData.filter(req => req.status === 'Pending').length;
                    const approvedRequests = requestsData.filter(req => req.status === 'Approved').length;
                    const acceptedRequests = requestsData.filter(req => req.status === 'Accepted').length;
                    const rejectedRequests = requestsData.filter(req => req.status === 'Rejected').length;

                    setStats(prev => ({
                        ...prev,
                        totalRequests,
                        pendingRequests,
                        approvedRequests,
                        acceptedRequests,
                        rejectedRequests
                    }));
                }

            } catch (error) {
                console.error('Error fetching dashboard data:', error);
            }
        };

        const handleLogout = () => {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('userType');
            navigate('/login');
        };

        // Woreda Admin Management Functions
        const handleWoredaSubmit = async(e) => {
            e.preventDefault();
            setWoredaError('');

            if (woredaForm.password !== woredaForm.confirmPassword && !editingWoreda) {
                setWoredaError('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                let url, method;

                if (editingWoreda) {
                    url = `http://localhost:5000/api/admins/edit/${editingWoreda.id}`;
                    method = 'PUT';
                } else {
                    url = 'http://localhost:5000/api/admins/register';
                    method = 'POST';
                }

                const submitData = {
                    fullName: woredaForm.fullName,
                    phoneNumber: woredaForm.phoneNumber,
                    password: woredaForm.password,
                    role: 'Woreda',
                    region_name: user.region_name,
                    zone_name: user.zone_name,
                    woreda_name: woredaForm.woreda_name
                };

                if (editingWoreda && !submitData.password) {
                    delete submitData.password;
                }

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(submitData)
                });

                const data = await response.json();

                if (response.ok) {
                    setShowWoredaForm(false);
                    setEditingWoreda(null);
                    setWoredaForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        woreda_name: ''
                    });
                    fetchDashboardData();
                    alert(editingWoreda ? 'Woreda admin updated successfully' : 'Woreda admin registered successfully');
                } else {
                    setWoredaError(data.message || 'Error processing woreda admin');
                }
            } catch (error) {
                console.error('Error processing woreda admin:', error);
                setWoredaError('Server error occurred');
            }
        };

        const deleteWoredaAdmin = async(adminId) => {
            if (!window.confirm('Are you sure you want to delete this woreda admin?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/delete/${adminId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Woreda admin deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting woreda admin');
                }
            } catch (error) {
                console.error('Error deleting woreda admin:', error);
                alert('Error deleting woreda admin');
            }
        };

        const editWoredaAdmin = (admin) => {
            setEditingWoreda(admin);
            setWoredaForm({
                fullName: admin.full_name || '',
                phoneNumber: admin.phone_number || '',
                password: '',
                confirmPassword: '',
                woreda_name: admin.woreda_name || ''
            });
            setShowWoredaForm(true);
        };

        // Farmer Management Functions
        const handleFarmerSubmit = async(e) => {
            e.preventDefault();
            setFarmerError('');

            if (farmerForm.password !== farmerForm.confirmPassword && !editingFarmer) {
                setFarmerError('Passwords do not match');
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const url = 'http://localhost:5000/api/admins/createfarmer';
                const method = 'POST';

                const submitData = {
                    fullName: farmerForm.fullName,
                    phoneNumber: farmerForm.phoneNumber,
                    password: farmerForm.password,
                    role: 'Farmer',
                    region_name: user.region_name,
                    zone_name: user.zone_name,
                    woreda_name: farmerForm.woreda_name,
                    kebele_name: farmerForm.kebele_name
                };

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(submitData)
                });

                const data = await response.json();

                if (response.ok) {
                    setShowFarmerForm(false);
                    setEditingFarmer(null);
                    setFarmerForm({
                        fullName: '',
                        phoneNumber: '',
                        password: '',
                        confirmPassword: '',
                        woreda_name: '',
                        kebele_name: ''
                    });
                    fetchDashboardData();
                    alert('Farmer registered successfully');
                } else {
                    setFarmerError(data.message || 'Error processing farmer');
                }
            } catch (error) {
                console.error('Error processing farmer:', error);
                setFarmerError('Server error occurred');
            }
        };

        // Product Management Functions
        const handleProductSubmit = async(e) => {
            e.preventDefault();
            try {
                const token = localStorage.getItem('token');

                const url = editingProduct ?
                    `http://localhost:5000/api/admins/editproduct/${editingProduct.id}` :
                    'http://localhost:5000/api/admins/addproduct';

                const method = editingProduct ? 'PUT' : 'POST';

                const response = await fetch(url, {
                    method,
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(productForm)
                });

                if (response.ok) {
                    setShowProductForm(false);
                    setEditingProduct(null);
                    setProductForm({ name: '', category: '', amount: '', description: '', price: '' });
                    fetchDashboardData();
                    alert(editingProduct ? 'Product updated successfully' : 'Product added successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error processing product');
                }
            } catch (error) {
                console.error('Error processing product:', error);
                alert('Error processing product');
            }
        };

        const deleteProduct = async(productId) => {
            if (!window.confirm('Are you sure you want to delete this product?')) {
                return;
            }

            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/deleteproduct/${productId}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    fetchDashboardData();
                    alert('Product deleted successfully');
                } else {
                    const errorData = await response.json();
                    alert(errorData.message || 'Error deleting product');
                }
            } catch (error) {
                console.error('Error deleting product:', error);
                alert('Error deleting product');
            }
        };

        const editProduct = (product) => {
            setEditingProduct(product);
            setProductForm({
                name: product.name || '',
                category: product.category || '',
                amount: product.amount || '',
                description: product.description || '',
                price: product.price || ''
            });
            setShowProductForm(true);
        };

        // Request Management Functions
        const updateRequestStatus = async(requestId, status, reason = '') => {
            try {
                const token = localStorage.getItem('token');

                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify({
                        status: status,
                        feedback: reason
                    })
                });

                const responseData = await response.json();

                if (response.ok) {
                    setShowRequestModal(false);
                    setSelectedRequest(null);
                    setRequestStatus('');
                    setDecisionReason('');
                    fetchDashboardData();
                    alert(`Request ${status} successfully`);
                } else {
                    alert(responseData.message || 'Error updating request');
                }
            } catch (error) {
                console.error('Error updating request:', error);
                alert('Error updating request');
            }
        };

        const handleStatusUpdate = (request, status) => {
            setSelectedRequest(request);
            setRequestStatus(status);
            setShowRequestModal(true);
        };

        const confirmStatusUpdate = () => {
            if (selectedRequest && requestStatus) {
                updateRequestStatus(selectedRequest.id, requestStatus, decisionReason);
            }
        };

        const handleDeleteRequest = async(requestId) => {
            if (window.confirm('Are you sure you want to delete this request?')) {
                try {
                    const token = localStorage.getItem('token');
                    const response = await fetch(`http://localhost:5000/api/admins/deleterequest/${requestId}`, {
                        method: 'DELETE',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });

                    if (response.ok) {
                        fetchDashboardData();
                        alert('Request deleted successfully');
                    } else {
                        const errorData = await response.json();
                        alert(errorData.message || 'Error deleting request');
                    }
                } catch (error) {
                    console.error('Error deleting request:', error);
                    alert('Error deleting request');
                }
            }
        };

        const getStatusBadge = (status) => {
            const statusClasses = {
                'Pending': 'bg-yellow-100 text-yellow-800',
                'Approved': 'bg-blue-100 text-blue-800',
                'Accepted': 'bg-green-100 text-green-800',
                'Rejected': 'bg-red-100 text-red-800'
            };

            return ( <
                span className = { `px-2 py-1 rounded-full text-xs font-medium ${statusClasses[status] || ''}` } > { status } <
                /span>
            );
        };

        const getLevelStatus = (level, request) => {
            const status = request[`${level}_status`];
            const admin = request[`${level}_admin_name`];
            const feedback = request[`${level}_feedback`];
            const date = request[`${level}_approved_at`];

            const formatLevel = level.charAt(0).toUpperCase() + level.slice(1);

            return ( <
                div className = "mb-4 p-3 bg-gray-50 rounded" >
                <
                h4 className = "font-semibold" > { formatLevel }
                Level < /h4> <
                p > < strong > Status: < /strong> {status || 'Pending'}</p > { admin && < p > < strong > Admin: < /strong> {admin}</p > } { feedback && < p > < strong > Feedback: < /strong> {feedback}</p > } { date && < p > < strong > Date: < /strong> {new Date(date).toLocaleString()}</p > } <
                /div>
            );
        };

        const viewRequestDetails = async(requestId) => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch(`http://localhost:5000/api/admins/requests/${requestId}/status`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    const requestDetails = await response.json();
                    setSelectedRequest(requestDetails);
                    setShowRequestModal(true);
                } else {
                    alert('Error fetching request details');
                }
            } catch (error) {
                console.error('Error fetching request details:', error);
                alert('Error fetching request details');
            }
        };

        // Check if Zone admin can act on this request
        const canZoneAdminAct = (request) => {
            // Zone admin can only act on requests that have been approved by Woreda admin
            return request.woreda_status === 'Approved' && request.zone_status === 'Pending';
        };

        // Check if admin can delete this request (only rejected ones)
        const canDeleteRequest = (request) => {
            return request.status === 'Rejected' && request.zone_status === 'Rejected';
        };

        if (!user) {
            return <div className = "flex justify-center items-center h-screen" > Loading... < /div>;
        }

        return ( <
                div className = "flex h-screen bg-gray-100" > { /* Sidebar */ } <
                div className = "w-64 bg-blue-800 text-white" >
                <
                div className = "p-4" >
                <
                h2 className = "text-xl font-bold" > Zone Dashboard < /h2> <
                /div> <
                nav className = "mt-6" >
                <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'overview' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('overview') } >
                Overview <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'woredas' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('woredas') } >
                Woreda Admins <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'farmers' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('farmers') } >
                Farmers <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'products' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('products') } >
                Products <
                /button> <
                button className = { `w-full text-left px-4 py-2 ${activeTab === 'requests' ? 'bg-blue-700' : 'hover:bg-blue-700'}` }
                onClick = {
                    () => setActiveTab('requests') } >
                Requests <
                /button> <
                /nav> <
                /div>

                { /* Main Content */ } <
                div className = "flex-1 overflow-auto" >
                <
                header className = "bg-white shadow p-4 flex justify-between items-center" >
                <
                div >
                <
                h1 className = "text-xl font-bold" > Welcome, { user.fullName }(Zone Admin) < /h1> <
                p className = "text-gray-600" > Region: { user.region_name } | Zone: { user.zone_name } < /p> <
                /div> <
                button onClick = { handleLogout }
                className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
                Logout <
                /button> <
                /header>

                <
                main className = "p-4" > { /* Overview Tab */ } {
                    activeTab === 'overview' && ( <
                        div className = "overview-tab" >
                        <
                        h2 className = "text-2xl font-bold mb-4" > System Overview < /h2> <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6" >
                        <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalRequests } < /h3> <
                        p > Total Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.pendingRequests } < /h3> <
                        p > Pending Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.approvedRequests } < /h3> <
                        p > Approved Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.acceptedRequests } < /h3> <
                        p > Accepted Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.rejectedRequests } < /h3> <
                        p > Rejected Requests < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalWoredaAdmins } < /h3> <
                        p > Woreda Admins < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalFarmers } < /h3> <
                        p > Farmers < /p> <
                        /div> <
                        div className = "bg-white p-4 rounded shadow" >
                        <
                        h3 className = "text-2xl font-bold" > { stats.totalProducts } < /h3> <
                        p > Products < /p> <
                        /div> <
                        /div>

                        <
                        div className = "quick-actions bg-white p-4 rounded shadow" >
                        <
                        h2 className = "text-xl font-bold mb-4" > Quick Actions < /h2> <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" >
                        <
                        button onClick = {
                            () => setActiveTab('requests') }
                        className = "bg-blue-500 hover:bg-blue-600 text-white p-3 rounded" >
                        Manage Requests <
                        /button> <
                        button onClick = {
                            () => setActiveTab('woredas') }
                        className = "bg-blue-500 hover:bg-blue-600 text-white p-3 rounded" >
                        Manage Woreda Admins <
                        /button> <
                        button onClick = {
                            () => setActiveTab('farmers') }
                        className = "bg-blue-500 hover:bg-blue-600 text-white p-3 rounded" >
                        View Farmers <
                        /button> <
                        button onClick = {
                            () => setActiveTab('products') }
                        className = "bg-blue-500 hover:bg-blue-600 text-white p-3 rounded" >
                        Manage Products <
                        /button> <
                        button onClick = {
                            () => setShowWoredaForm(true) }
                        className = "bg-green-500 hover:bg-green-600 text-white p-3 rounded" >
                        Register Woreda Admin <
                        /button> <
                        button onClick = {
                            () => setShowProductForm(true) }
                        className = "bg-green-500 hover:bg-green-600 text-white p-3 rounded" >
                        Add Product <
                        /button> <
                        /div> <
                        /div> <
                        /div>
                    )
                }

                { /* Woreda Admins Tab */ } {
                    activeTab === 'woredas' && ( <
                        div className = "woredas-tab" >
                        <
                        div className = "flex justify-between items-center mb-4" >
                        <
                        h2 className = "text-2xl font-bold" > Woreda Admin Management < /h2> <
                        button onClick = {
                            () => {
                                setEditingWoreda(null);
                                setWoredaForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    woreda_name: ''
                                });
                                setShowWoredaForm(true);
                            }
                        }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
                        Register Woreda Admin <
                        /button> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            woredaAdmins.map((admin) => ( <
                                div key = { admin.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold" > { admin.full_name } < /h3> <
                                p className = "text-gray-600" > Phone: { admin.phone_number } < /p> <
                                p className = "text-gray-600" > Woreda: { admin.woreda_name } < /p> <
                                p className = "text-gray-600" > Registered: { new Date(admin.created_at).toLocaleDateString() } < /p> <
                                div className = "mt-4 flex space-x-2" >
                                <
                                button onClick = {
                                    () => editWoredaAdmin(admin) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" >
                                Edit <
                                /button> <
                                button onClick = {
                                    () => deleteWoredaAdmin(admin.id) }
                                className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" >
                                Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } {
                            woredaAdmins.length === 0 && ( <
                                p className = "text-gray-600" > No Woreda admins found in your zone. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Farmers Tab */ } {
                    activeTab === 'farmers' && ( <
                        div className = "farmers-tab" >
                        <
                        div className = "mb-4" >
                        <
                        h2 className = "text-2xl font-bold mb-2" > Farmer Management < /h2> <
                        p className = "text-gray-600" > Viewing farmers in your zone: { user.zone_name } < /p>

                        <
                        div className = "flex items-center space-x-2 mt-4" >
                        <
                        label className = "font-medium" > Filter by Woreda: < /label> <
                        select value = { woredaFilter }
                        onChange = {
                            (e) => setWoredaFilter(e.target.value) }
                        className = "border rounded p-1" >
                        <
                        option value = "all" > All Woredas < /option> {
                            woredas.map(woreda => ( <
                                option key = { woreda }
                                value = { woreda } > { woreda } < /option>
                            ))
                        } <
                        /select> <
                        /div> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            farmers
                            .filter(farmer => woredaFilter === 'all' || farmer.woreda_name === woredaFilter)
                            .map((farmer) => ( <
                                div key = { farmer.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                div className = "farmer-info" >
                                <
                                h3 className = "text-lg font-semibold" > { farmer.full_name } < /h3> <
                                p className = "text-gray-600" > Phone: { farmer.phone_number } < /p> <
                                p className = "text-gray-600" > Woreda: { farmer.woreda_name } < /p> <
                                p className = "text-gray-600" > Kebele: { farmer.kebele_name } < /p> <
                                p className = "text-gray-600" > Registered: { new Date(farmer.created_at).toLocaleDateString() } < /p> <
                                /div> <
                                div className = "mt-4" >
                                <
                                h4 className = "font-medium" > Recent Requests: < /h4> {
                                    requests
                                        .filter(req => req.farmer_id === farmer.id)
                                        .slice(0, 3)
                                        .map(req => ( <
                                            div key = { req.id }
                                            className = "flex justify-between items-center mt-2" >
                                            <
                                            span className = "text-sm" > { req.product_name } < /span> { getStatusBadge(req.status) } <
                                            /div>
                                        ))
                                } {
                                    requests.filter(req => req.farmer_id === farmer.id).length === 0 && ( <
                                        p className = "text-gray-500 text-sm mt-2" > No requests from this farmer < /p>
                                    )
                                } <
                                /div> <
                                /div>
                            ))
                        } {
                            farmers.length === 0 && ( <
                                p className = "text-gray-600" > No farmers found in your zone. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Products Tab */ } {
                    activeTab === 'products' && ( <
                        div className = "products-tab" >
                        <
                        div className = "flex justify-between items-center mb-4" >
                        <
                        h2 className = "text-2xl font-bold" > Product Management < /h2> <
                        button onClick = {
                            () => setShowProductForm(true) }
                        className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
                        Add Product <
                        /button> <
                        /div>

                        <
                        div className = "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4" > {
                            products.map((product) => ( <
                                div key = { product.id }
                                className = "bg-white p-4 rounded shadow" >
                                <
                                h3 className = "text-lg font-semibold" > { product.name } < /h3> <
                                p className = "text-gray-600" > { product.description } < /p> <
                                p className = "text-gray-600" > Category: { product.category } < /p> <
                                p className = "text-gray-600" > Amount: { product.amount } < /p> <
                                p className = "text-gray-600" > Price: { product.price } < /p> <
                                p className = "text-gray-600" > Created: { new Date(product.created_at).toLocaleDateString() } < /p> <
                                div className = "mt-4 flex space-x-2" >
                                <
                                button onClick = {
                                    () => editProduct(product) }
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" >
                                Edit <
                                /button> <
                                button onClick = {
                                    () => deleteProduct(product.id) }
                                className = "bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" >
                                Delete <
                                /button> <
                                /div> <
                                /div>
                            ))
                        } {
                            products.length === 0 && ( <
                                p className = "text-gray-600" > No products found. < /p>
                            )
                        } <
                        /div> <
                        /div>
                    )
                }

                { /* Requests Tab */ } {
                    activeTab === 'requests' && ( <
                            div className = "requests-tab" >
                            <
                            div className = "mb-4" >
                            <
                            h2 className = "text-2xl font-bold mb-2" > Request Management < /h2> <
                            p className = "text-gray-600" > Manage farmer requests in your zone: { user.zone_name } < /p>

                            <
                            div className = "flex items-center space-x-4 mt-4" >
                            <
                            div className = "flex items-center space-x-2" >
                            <
                            label className = "font-medium" > Filter by Status: < /label> <
                            select value = { statusFilter }
                            onChange = {
                                (e) => setStatusFilter(e.target.value) }
                            className = "border rounded p-1" >
                            <
                            option value = "all" > All Requests < /option> <
                            option value = "Pending" > Pending < /option> <
                            option value = "Approved" > Approved < /option> <
                            option value = "Accepted" > Accepted < /option> <
                            option value = "Rejected" > Rejected < /option> <
                            /select> <
                            /div>

                            <
                            div className = "flex items-center space-x-2" >
                            <
                            label className = "font-medium" > Filter by Woreda: < /label> <
                            select value = { woredaFilter }
                            onChange = {
                                (e) => setWoredaFilter(e.target.value) }
                            className = "border rounded p-1" >
                            <
                            option value = "all" > All Woredas < /option> {
                                woredas.map(woreda => ( <
                                    option key = { woreda }
                                    value = { woreda } > { woreda } < /option>
                                ))
                            } <
                            /select> <
                            /div> <
                            /div> <
                            /div>

                            <
                            div className = "grid grid-cols-1 gap-4" > {
                                filteredRequests.map((request) => ( <
                                        div key = { request.id }
                                        className = "bg-white p-4 rounded shadow" >
                                        <
                                        div className = "flex flex-col md:flex-row justify-between" >
                                        <
                                        div className = "request-info flex-1" >
                                        <
                                        h3 className = "text-lg font-semibold" > { request.product_name } < /h3> <
                                        p className = "text-gray-600" > Farmer: { request.farmer_name }({ request.farmer_phone }) < /p> <
                                        p className = "text-gray-600" > Quantity: { request.quantity } < /p> {
                                            request.note && < p className = "text-gray-600" > Note: { request.note } < /p>} <
                                                p className = "text-gray-600" > Product Amount: { request.product_amount } < /p> <
                                                p className = "text-gray-600" > Woreda: { request.woreda_name } < /p> <
                                                p className = "text-gray-600" > Date: { new Date(request.created_at).toLocaleDateString() } < /p> <
                                                div className = "mt-2" > { getStatusBadge(request.status) } <
                                                /div> {
                                                    request.handled_by_admin && ( <
                                                        p className = "text-gray-600" > Handled by: { request.handled_by_admin } < /p>
                                                    )
                                                } <
                                                button
                                            onClick = {
                                                () => viewRequestDetails(request.id) }
                                            className = "mt-2 bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm" >
                                                View Details <
                                                /button> <
                                                /div> <
                                                div className = "request-actions mt-4 md:mt-0 md:ml-4" > {
                                                    canZoneAdminAct(request) && ( <
                                                        div className = "status-options space-y-2" >
                                                        <
                                                        button onClick = {
                                                            () => handleStatusUpdate(request, 'Approved') }
                                                        className = "w-full bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded" >
                                                        Approve <
                                                        /button> <
                                                        button onClick = {
                                                            () => handleStatusUpdate(request, 'Accepted') }
                                                        className = "w-full bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded" >
                                                        Accept <
                                                        /button> <
                                                        button onClick = {
                                                            () => handleStatusUpdate(request, 'Rejected') }
                                                        className = "w-full bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded" >
                                                        Reject <
                                                        /button> <
                                                        /div>
                                                    )
                                                } {
                                                    !canZoneAdminAct(request) && ( <
                                                        p className = "text-gray-500 text-sm" > Not available
                                                        for action < /p>
                                                    )
                                                } {
                                                    canDeleteRequest(request) && ( <
                                                        button onClick = {
                                                            () => handleDeleteRequest(request.id) }
                                                        className = "mt-2 bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded w-full" >
                                                        Delete Request <
                                                        /button>
                                                    )
                                                } <
                                                /div> <
                                                /div> <
                                                /div>
                                        ))
                                } {
                                    filteredRequests.length === 0 && ( <
                                        p className = "text-gray-600" > No requests found with the selected filters. < /p>
                                    )
                                } <
                                /div> <
                                /div>
                            )
                        } <
                        /main> <
                        /div>

                    { /* Modal for Woreda Admin Form */ } {
                        showWoredaForm && ( <
                            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                            <
                            div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
                            <
                            h2 className = "text-xl font-bold mb-4" > { editingWoreda ? 'Edit Woreda Admin' : 'Register Woreda Admin' } <
                            /h2> <
                            form onSubmit = { handleWoredaSubmit } >
                            <
                            div className = "mb-4" >
                            <
                            label className = "block text-gray-700 mb-2" > Full Name < /label> <
                            input type = "text"
                            value = { woredaForm.fullName }
                            onChange = {
                                (e) => setWoredaForm({...woredaForm, fullName: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required /
                            >
                            <
                            /div> <
                            div className = "mb-4" >
                            <
                            label className = "block text-gray-700 mb-2" > Phone Number < /label> <
                            input type = "text"
                            value = { woredaForm.phoneNumber }
                            onChange = {
                                (e) => setWoredaForm({...woredaForm, phoneNumber: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required /
                            >
                            <
                            /div> <
                            div className = "mb-4" >
                            <
                            label className = "block text-gray-700 mb-2" > Woreda Name < /label> <
                            input type = "text"
                            value = { woredaForm.woreda_name }
                            onChange = {
                                (e) => setWoredaForm({...woredaForm, woreda_name: e.target.value }) }
                            className = "w-full border rounded p-2"
                            required /
                            >
                            <
                            /div> {
                                !editingWoreda && ( <
                                    >
                                    <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 mb-2" > Password < /label> <
                                    input type = "password"
                                    value = { woredaForm.password }
                                    onChange = {
                                        (e) => setWoredaForm({...woredaForm, password: e.target.value }) }
                                    className = "w-full border rounded p-2"
                                    required = {!editingWoreda }
                                    /> <
                                    /div> <
                                    div className = "mb-4" >
                                    <
                                    label className = "block text-gray-700 mb-2" > Confirm Password < /label> <
                                    input type = "password"
                                    value = { woredaForm.confirmPassword }
                                    onChange = {
                                        (e) => setWoredaForm({...woredaForm, confirmPassword: e.target.value }) }
                                    className = "w-full border rounded p-2"
                                    required = {!editingWoreda }
                                    /> <
                                    /div> <
                                    />
                                )
                            } {
                                woredaError && < p className = "text-red-500 mb-4" > { woredaError } < /p>} <
                                    div className = "flex justify-end space-x-2" >
                                    <
                                    button
                                type = "button"
                                onClick = {
                                    () => {
                                        setShowWoredaForm(false);
                                        setEditingWoreda(null);
                                        setWoredaError('');
                                    }
                                }
                                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded" >
                                    Cancel <
                                    /button> <
                                    button
                                type = "submit"
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                                    { editingWoreda ? 'Update' : 'Register' } <
                                    /button> <
                                    /div> <
                                    /form> <
                                    /div> <
                                    /div>
                            )
                        }

                        { /* Modal for Product Form */ } {
                            showProductForm && ( <
                                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                <
                                div className = "bg-white p-6 rounded shadow-lg w-full max-w-md" >
                                <
                                h2 className = "text-xl font-bold mb-4" > { editingProduct ? 'Edit Product' : 'Add Product' } <
                                /h2> <
                                form onSubmit = { handleProductSubmit } >
                                <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 mb-2" > Product Name < /label> <
                                input type = "text"
                                value = { productForm.name }
                                onChange = {
                                    (e) => setProductForm({...productForm, name: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 mb-2" > Category < /label> <
                                input type = "text"
                                value = { productForm.category }
                                onChange = {
                                    (e) => setProductForm({...productForm, category: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 mb-2" > Amount < /label> <
                                input type = "text"
                                value = { productForm.amount }
                                onChange = {
                                    (e) => setProductForm({...productForm, amount: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 mb-2" > Price < /label> <
                                input type = "text"
                                value = { productForm.price }
                                onChange = {
                                    (e) => setProductForm({...productForm, price: e.target.value }) }
                                className = "w-full border rounded p-2"
                                required /
                                >
                                <
                                /div> <
                                div className = "mb-4" >
                                <
                                label className = "block text-gray-700 mb-2" > Description < /label> <
                                textarea value = { productForm.description }
                                onChange = {
                                    (e) => setProductForm({...productForm, description: e.target.value }) }
                                className = "w-full border rounded p-2"
                                rows = "3" >
                                < /textarea> <
                                /div> <
                                div className = "flex justify-end space-x-2" >
                                <
                                button type = "button"
                                onClick = {
                                    () => {
                                        setShowProductForm(false);
                                        setEditingProduct(null);
                                    }
                                }
                                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded" >
                                Cancel <
                                /button> <
                                button type = "submit"
                                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                                { editingProduct ? 'Update' : 'Add' } <
                                /button> <
                                /div> <
                                /form> <
                                /div> <
                                /div>
                            )
                        }

                        { /* Modal for Request Details and Status Update */ } {
                            showRequestModal && ( <
                                div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" >
                                <
                                div className = "bg-white p-6 rounded shadow-lg w-full max-w-2xl max-h-screen overflow-y-auto" >
                                <
                                h2 className = "text-xl font-bold mb-4" > Request Details < /h2>

                                {
                                    selectedRequest && ( <
                                        div className = "request-details" >
                                        <
                                        div className = "mb-4 p-3 bg-gray-50 rounded" >
                                        <
                                        h3 className = "text-lg font-semibold" > { selectedRequest.product_name } < /h3> <
                                        p > < strong > Farmer: < /strong> {selectedRequest.farmer_name}</p >
                                        <
                                        p > < strong > Phone: < /strong> {selectedRequest.farmer_phone}</p >
                                        <
                                        p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                                        <
                                        p > < strong > Note: < /strong> {selectedRequest.note || 'None'}</p >
                                        <
                                        p > < strong > Product Amount: < /strong> {selectedRequest.product_amount}</p >
                                        <
                                        p > < strong > Woreda: < /strong> {selectedRequest.woreda_name}</p >
                                        <
                                        p > < strong > Kebele: < /strong> {selectedRequest.kebele_name}</p >
                                        <
                                        p > < strong > Date: < /strong> {new Date(selectedRequest.created_at).toLocaleString()}</p >
                                        <
                                        div className = "mt-2" >
                                        <
                                        strong > Status: < /strong> {getStatusBadge(selectedRequest.status)} <
                                        /div> <
                                        /div>

                                        { /* Show status at each level */ } { getLevelStatus('kebele', selectedRequest) } { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) }

                                        {
                                            canZoneAdminAct(selectedRequest) && ( <
                                                div className = "mt-4" >
                                                <
                                                h3 className = "font-semibold mb-2" > Update Status < /h3> <
                                                div className = "mb-4" >
                                                <
                                                label className = "block text-gray-700 mb-2" > Decision Reason(Optional) < /label> <
                                                textarea value = { decisionReason }
                                                onChange = {
                                                    (e) => setDecisionReason(e.target.value) }
                                                className = "w-full border rounded p-2"
                                                rows = "3"
                                                placeholder = "Enter reason for your decision" >
                                                < /textarea> <
                                                /div> <
                                                div className = "flex space-x-2" >
                                                <
                                                button onClick = {
                                                    () => updateRequestStatus(selectedRequest.id, 'Approved', decisionReason) }
                                                className = "bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded" >
                                                Approve <
                                                /button> <
                                                button onClick = {
                                                    () => updateRequestStatus(selectedRequest.id, 'Accepted', decisionReason) }
                                                className = "bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded" >
                                                Accept <
                                                /button> <
                                                button onClick = {
                                                    () => updateRequestStatus(selectedRequest.id, 'Rejected', decisionReason) }
                                                className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
                                                Reject <
                                                /button> <
                                                /div> <
                                                /div>
                                            )
                                        }

                                        {
                                            canDeleteRequest(selectedRequest) && ( <
                                                div className = "mt-4" >
                                                <
                                                button onClick = {
                                                    () => handleDeleteRequest(selectedRequest.id) }
                                                className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded" >
                                                Delete Request <
                                                /button> <
                                                /div>
                                            )
                                        } <
                                        /div>
                                    )
                                }

                                <
                                div className = "mt-6" >
                                <
                                button onClick = {
                                    () => {
                                        setShowRequestModal(false);
                                        setSelectedRequest(null);
                                        setRequestStatus('');
                                        setDecisionReason('');
                                    }
                                }
                                className = "bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded" >
                                Close <
                                /button> <
                                /div> <
                                /div> <
                                /div>
                            )
                        } <
                        /div>
                    );
                };

                export default ZoneDashboard;
