import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegionDashboard.css';

const RegionDashboard = () => {
    const [activeTab, setActiveTab] = useState('overview');
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // Product Management State
    const [products, setProducts] = useState([]);
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [productForm, setProductForm] = useState({
        name: '',
        category: '',
        description: ''
    });

    // Zone Admin Management State
    const [zoneAdmins, setZoneAdmins] = useState([]);
    const [showZoneAdminForm, setShowZoneAdminForm] = useState(false);
    const [editingZoneAdmin, setEditingZoneAdmin] = useState(null);
    const [zoneAdminForm, setZoneAdminForm] = useState({
        fullName: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        role: 'Zone',
        zone_name: ''
    });
    const [zoneAdminError, setZoneAdminError] = useState('');

    // Requests State
    const [requests, setRequests] = useState([]);
    const [selectedRequest, setSelectedRequest] = useState(null);

    // Stats State
    const [stats, setStats] = useState({
        totalRequests: 0,
        pendingRequests: 0,
        approvedRequests: 0,
        rejectedRequests: 0,
        totalZoneAdmins: 0,
        totalProducts: 0
    });

    useEffect(() => {
        // Check authentication
        const token = localStorage.getItem('token');
        const userData = JSON.parse(localStorage.getItem('user') || 'null');
        const userType = localStorage.getItem('userType');

        if (!token || userType !== 'Region' || !userData) {
            navigate('/login');
            return;
        }

        setUser(userData);
        fetchDashboardData();
    }, [navigate]);

    const fetchDashboardData = async() => {
        try {
            const token = localStorage.getItem('token');

            // Fetch products in this region
            const productsResponse = await fetch('http://localhost:5000/api/region/products', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (productsResponse.ok) {
                const productsData = await productsResponse.json();
                setProducts(productsData);
                setStats(prev => ({...prev, totalProducts: productsData.length }));
            }

            // Fetch zone admins in this region
            const zoneAdminsResponse = await fetch('http://localhost:5000/api/region/zone-admins', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (zoneAdminsResponse.ok) {
                const zoneAdminsData = await zoneAdminsResponse.json();
                setZoneAdmins(zoneAdminsData);
                setStats(prev => ({...prev, totalZoneAdmins: zoneAdminsData.length }));
            }

            // Fetch requests from this region
            const requestsResponse = await fetch('http://localhost:5000/api/region/requests', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (requestsResponse.ok) {
                const requestsData = await requestsResponse.json();
                setRequests(requestsData);

                // Calculate stats from requests data
                const totalRequests = requestsData.length;
                const pendingRequests = requestsData.filter(req => req.status === 'pending').length;
                const approvedRequests = requestsData.filter(req => req.status === 'accepted').length;
                const rejectedRequests = requestsData.filter(req => req.status === 'rejected').length;

                setStats(prev => ({
                    ...prev,
                    totalRequests,
                    pendingRequests,
                    approvedRequests,
                    rejectedRequests
                }));
            }

        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('userType');
        navigate('/login');
    };

    // Product Management Functions
    const handleProductSubmit = async(e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const url = editingProduct ?
                `http://localhost:5000/api/region/products/${editingProduct.id}` :
                'http://localhost:5000/api/region/products';

            const method = editingProduct ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(productForm)
            });

            if (response.ok) {
                setShowProductForm(false);
                setEditingProduct(null);
                setProductForm({ name: '', category: '', description: '' });
                fetchDashboardData();
            } else {
                const errorData = await response.json();
                console.error('Error saving product:', errorData.message);
            }
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const deleteProduct = async(productId) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/region/products/${productId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            }
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    // Zone Admin Management Functions
    const handleZoneAdminSubmit = async(e) => {
        e.preventDefault();
        setZoneAdminError('');

        if (zoneAdminForm.password !== zoneAdminForm.confirmPassword && !editingZoneAdmin) {
            setZoneAdminError('Passwords do not match');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            let url, method;

            if (editingZoneAdmin) {
                // Update existing zone admin
                url = `http://localhost:5000/api/region/zone-admins/${editingZoneAdmin.id}`;
                method = 'PUT';
            } else {
                // Create new zone admin
                url = 'http://localhost:5000/api/region/zone-admins';
                method = 'POST';
            }

            // Prepare data for submission (don't send password if editing and password is empty)
            const submitData = {...zoneAdminForm };
            if (editingZoneAdmin && !submitData.password) {
                delete submitData.password;
                delete submitData.confirmPassword;
            }

            const response = await fetch(url, {
                method,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(submitData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowZoneAdminForm(false);
                setEditingZoneAdmin(null);
                setZoneAdminForm({
                    fullName: '',
                    phoneNumber: '',
                    password: '',
                    confirmPassword: '',
                    role: 'Zone',
                    zone_name: ''
                });
                fetchDashboardData();
            } else {
                setZoneAdminError(data.message || 'Error processing zone admin');
            }
        } catch (error) {
            console.error('Error processing zone admin:', error);
            setZoneAdminError('Server error occurred');
        }
    };

    const deleteZoneAdmin = async(zoneAdminId) => {
        if (!window.confirm('Are you sure you want to delete this zone admin?')) {
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/region/zone-admins/${zoneAdminId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                fetchDashboardData();
            }
        } catch (error) {
            console.error('Error deleting zone admin:', error);
        }
    };

    const editZoneAdmin = (zoneAdmin) => {
        setEditingZoneAdmin(zoneAdmin);
        setZoneAdminForm({
            fullName: zoneAdmin.full_name || '',
            phoneNumber: zoneAdmin.phone_number || '',
            password: '',
            confirmPassword: '',
            role: zoneAdmin.role || 'Zone',
            zone_name: zoneAdmin.zone_name || ''
        });
        setShowZoneAdminForm(true);
    };

    // Request Management Functions
    const handleRequestAction = async(requestId, action) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api//requests/${requestId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ action })
            });

            if (response.ok) {
                setSelectedRequest(null);
                fetchDashboardData(); // Refresh requests
            }
        } catch (error) {
            console.error('Error processing request:', error);
        }
    };

    if (!user) {
        return <div className = "loading" > Loading... < /div>;
    }

    return ( <
        div className = "region-dashboard" > { /* Sidebar */ } <
        div className = "sidebar" >
        <
        h2 > Region Dashboard < /h2> <
        nav >
        <
        button className = { activeTab === 'overview' ? 'active' : '' }
        onClick = {
            () => setActiveTab('overview')
        } > 📊Overview < /button> <
        button className = { activeTab === 'products' ? 'active' : '' }
        onClick = {
            () => setActiveTab('products')
        } > 📦Products < /button> <
        button className = { activeTab === 'zone-admins' ? 'active' : '' }
        onClick = {
            () => setActiveTab('zone-admins')
        } > 👥Zone Admins < /button> <
        button className = { activeTab === 'requests' ? 'active' : '' }
        onClick = {
            () => setActiveTab('requests')
        } > 📋Requests < /button> < /
        nav > <
        /div>

        { /* Main Content */ } <
        div className = "main-content" >
        <
        header >
        <
        h1 > Welcome, { user.fullName }(Region Admin) < /h1> <
        p > Region: { user.region_name } < /p> <
        button onClick = { handleLogout }
        className = "logout-btn" >
        Logout <
        /button> < /
        header >

        <
        main > { /* Overview Tab */ } {
            activeTab === 'overview' && ( <
                div className = "overview-tab" >
                <
                h2 > Region Overview < /h2> <
                div className = "stats-grid" >
                <
                div className = "stat-card" >
                <
                h3 > { stats.totalRequests } < /h3> <
                p > Total Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.pendingRequests } < /h3> <
                p > Pending Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.approvedRequests } < /h3> <
                p > Approved Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.rejectedRequests } < /h3> <
                p > Rejected Requests < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalZoneAdmins } < /h3> <
                p > Zone Admins < /p> < /
                div > <
                div className = "stat-card" >
                <
                h3 > { stats.totalProducts } < /h3> <
                p > Products < /p> < /
                div > <
                /div>

                <
                div className = "quick-actions" >
                <
                h2 > Quick Actions < /h2> <
                div className = "actions-grid" >
                <
                button onClick = {
                    () => setActiveTab('zone-admins')
                }
                className = "action-btn" > 👥Create Zone Admin <
                /button> <
                button onClick = {
                    () => setActiveTab('products')
                }
                className = "action-btn" > 📦Add Product <
                /button> <
                button onClick = {
                    () => setActiveTab('requests')
                }
                className = "action-btn" > 📋Process Requests <
                /button> <
                button className = "action-btn" > 📊Region Reports <
                /button> < /
                div > <
                /div> < /
                div >
            )
        }

        { /* Products Tab */ } {
            activeTab === 'products' && ( <
                div className = "products-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Product Management < /h2> <
                button onClick = {
                    () => setShowProductForm(true)
                }
                className = "add-btn" >
                Add Product <
                /button> < /
                div >

                { /* Product Form Modal */ } {
                    showProductForm && ( <
                        div className = "modal" >
                        <
                        div className = "modal-content" >
                        <
                        h3 > { editingProduct ? 'Edit Product' : 'Add Product' } < /h3> <
                        form onSubmit = { handleProductSubmit } >
                        <
                        input type = "text"
                        placeholder = "Product Name"
                        value = { productForm.name }
                        onChange = {
                            (e) => setProductForm({...productForm, name: e.target.value })
                        }
                        required /
                        >
                        <
                        input type = "text"
                        placeholder = "Category"
                        value = { productForm.category }
                        onChange = {
                            (e) => setProductForm({...productForm, category: e.target.value })
                        }
                        required /
                        >
                        <
                        textarea placeholder = "Description"
                        value = { productForm.description }
                        onChange = {
                            (e) => setProductForm({...productForm, description: e.target.value })
                        }
                        rows = "3"
                        required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > Save < /button> <
                        button type = "button"
                        onClick = {
                            () => {
                                setShowProductForm(false);
                                setEditingProduct(null);
                                setProductForm({ name: '', category: '', description: '' });
                            }
                        } >
                        Cancel <
                        /button> < /
                        div > <
                        /form> < /
                        div > <
                        /div>
                    )
                }

                { /* Products List */ } <
                div className = "products-grid" > {
                    products.map((product) => ( <
                        div key = { product.id }
                        className = "product-card" >
                        <
                        h3 > { product.name } < /h3> <
                        p className = "category" > { product.category } < /p> <
                        p className = "description" > { product.description } < /p> <
                        div className = "product-actions" >
                        <
                        button onClick = {
                            () => {
                                setEditingProduct(product);
                                setProductForm({
                                    name: product.name,
                                    category: product.category,
                                    description: product.description
                                });
                                setShowProductForm(true);
                            }
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteProduct(product.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
                div >
            )
        }

        { /* Zone Admins Tab */ } {
            activeTab === 'zone-admins' && ( <
                div className = "zone-admins-tab" >
                <
                div className = "tab-header" >
                <
                h2 > Zone Admin Management < /h2> <
                button onClick = {
                    () => {
                        setEditingZoneAdmin(null);
                        setZoneAdminForm({
                            fullName: '',
                            phoneNumber: '',
                            password: '',
                            confirmPassword: '',
                            role: 'Zone',
                            zone_name: ''
                        });
                        setShowZoneAdminForm(true);
                    }
                }
                className = "add-btn" >
                Register Zone Admin <
                /button> < /
                div >

                { /* Zone Admin Form Modal */ } {
                    showZoneAdminForm && ( <
                            div className = "modal" >
                            <
                            div className = "modal-content" >
                            <
                            h3 > { editingZoneAdmin ? 'Edit Zone Admin' : 'Register Zone Admin' } < /h3> {
                            zoneAdminError && ( <
                                div className = "error-message" > { zoneAdminError } < /div>
                            )
                        } <
                        form onSubmit = { handleZoneAdminSubmit } >
                        <
                        input type = "text"
                    placeholder = "Full Name"
                    value = { zoneAdminForm.fullName }
                    onChange = {
                        (e) => setZoneAdminForm({...zoneAdminForm, fullName: e.target.value })
                    }
                    required /
                        >
                        <
                        input type = "tel"
                    placeholder = "Phone Number"
                    value = { zoneAdminForm.phoneNumber }
                    onChange = {
                        (e) => setZoneAdminForm({...zoneAdminForm, phoneNumber: e.target.value })
                    }
                    required /
                        >
                        {!editingZoneAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "Password"
                                value = { zoneAdminForm.password }
                                onChange = {
                                    (e) => setZoneAdminForm({...zoneAdminForm, password: e.target.value })
                                }
                                required /
                                >
                                <
                                input type = "password"
                                placeholder = "Confirm Password"
                                value = { zoneAdminForm.confirmPassword }
                                onChange = {
                                    (e) => setZoneAdminForm({...zoneAdminForm, confirmPassword: e.target.value })
                                }
                                required /
                                >
                                <
                                />
                            )
                        } {
                            editingZoneAdmin && ( <
                                >
                                <
                                input type = "password"
                                placeholder = "New Password (leave empty to keep current)"
                                value = { zoneAdminForm.password }
                                onChange = {
                                    (e) => setZoneAdminForm({...zoneAdminForm, password: e.target.value })
                                }
                                /> <
                                input type = "password"
                                placeholder = "Confirm New Password"
                                value = { zoneAdminForm.confirmPassword }
                                onChange = {
                                    (e) => setZoneAdminForm({...zoneAdminForm, confirmPassword: e.target.value })
                                }
                                /> < /
                                >
                            )
                        } <
                        input type = "text"
                    placeholder = "Zone Name"
                    value = { zoneAdminForm.zone_name }
                    onChange = {
                        (e) => setZoneAdminForm({...zoneAdminForm, zone_name: e.target.value })
                    }
                    required /
                        >
                        <
                        div className = "modal-actions" >
                        <
                        button type = "submit" > { editingZoneAdmin ? 'Update' : 'Register' } < /button> <
                    button type = "button"
                    onClick = {
                            () => {
                                setShowZoneAdminForm(false);
                                setEditingZoneAdmin(null);
                                setZoneAdminError('');
                                setZoneAdminForm({
                                    fullName: '',
                                    phoneNumber: '',
                                    password: '',
                                    confirmPassword: '',
                                    role: 'Zone',
                                    zone_name: ''
                                });
                            }
                        } >
                        Cancel <
                        /button> < /
                        div > <
                        /form> < /
                        div > <
                        /div>
                )
            }

            { /* Zone Admins List */ } <
            div className = "admins-list" > {
                    zoneAdmins.map((admin) => ( <
                        div key = { admin.id }
                        className = "admin-card" >
                        <
                        h3 > { admin.full_name } < /h3> <
                        p > Phone: { admin.phone_number } < /p> <
                        p > Role: { admin.role } < /p> <
                        p > Zone: { admin.zone_name } < /p> <
                        div className = "admin-actions" >
                        <
                        button onClick = {
                            () => editZoneAdmin(admin)
                        }
                        className = "edit-btn" >
                        Edit <
                        /button> <
                        button onClick = {
                            () => deleteZoneAdmin(admin.id)
                        }
                        className = "delete-btn" >
                        Delete <
                        /button> < /
                        div > <
                        /div>
                    ))
                } <
                /div> < /
                div >
        )
    }

    { /* Requests Tab */ } {
        activeTab === 'requests' && ( <
            div className = "requests-tab" >
            <
            h2 > Request Management < /h2>

            { /* Requests List */ } <
            div className = "requests-list" > {
                requests.map((request) => ( <
                    div key = { request.id }
                    className = "request-card" >
                    <
                    h3 > Request# { request.id } < /h3> <
                    p > Product: { request.product_name } < /p> <
                    p > Quantity: { request.quantity } < /p> <
                    p > Zone: { request.zone_name } < /p> <
                    p > Status: < span className = { `status-${request.status}` } > { request.status } < /span></p >
                    <
                    div className = "request-actions" > {
                        request.status === 'pending' && ( <
                            >
                            <
                            button onClick = {
                                () => handleRequestAction(request.id, 'approve')
                            }
                            className = "approve-btn" >
                            Approve <
                            /button> <
                            button onClick = {
                                () => handleRequestAction(request.id, 'reject')
                            }
                            className = "reject-btn" >
                            Reject <
                            /button> < /
                            >
                        )
                    } <
                    button onClick = {
                        () => setSelectedRequest(request)
                    }
                    className = "view-btn" >
                    View Details <
                    /button> < /
                    div > <
                    /div>
                ))
            } <
            /div>

            { /* Request Detail Modal */ } {
                selectedRequest && ( <
                    div className = "modal" >
                    <
                    div className = "modal-content" >
                    <
                    h3 > Request# { selectedRequest.id }
                    Details < /h3> <
                    div className = "request-details" >
                    <
                    p > < strong > Product: < /strong> {selectedRequest.product_name}</p >
                    <
                    p > < strong > Quantity: < /strong> {selectedRequest.quantity}</p >
                    <
                    p > < strong > Zone: < /strong> {selectedRequest.zone_name}</p >
                    <
                    p > < strong > Status: < /strong> {selectedRequest.status}</p >
                    <
                    p > < strong > Request Date: < /strong> {new Date(selectedRequest.created_at).toLocaleDateString()}</p > {
                        selectedRequest.notes && ( <
                            p > < strong > Notes: < /strong> {selectedRequest.notes}</p >
                        )
                    } <
                    /div> <
                    div className = "modal-actions" >
                    <
                    button onClick = {
                        () => setSelectedRequest(null)
                    } > Close < /button> < /
                    div > <
                    /div> < /
                    div >
                )
            } <
            /div>
        )
    } <
    /main> < /
    div > <
        /div>
);
};

export default RegionDashboard;
