import React from 'react';

function RequestsPage({ requests, currentUser, onApprove }) {
    return ( <
        div >
        <
        h2 > All Requests < /h2> <
        ul > {
            requests.map(req => ( <
                li key = { req.id } > { req.productName || 'Unknown' } - Status: { req.status || 'Pending' } {
                    req.status === 'Pending' && onApprove && ( <
                        button onClick = {
                            () => onApprove(req.id) } > Approve < /button>
                    )
                } <
                /li>
            ))
        } <
        /ul> <
        /div>
    );
}

export default RequestsPage;
