                    <
                    strong className = "dark:text-white" > Amount: < /strong> <
                    p className = "dark:text-gray-300" > { selectedRequest.amount } < /p> < /
                    div > <
                    div >
                    <
                    strong className = "dark:text-white" > Total Price: < /strong> <
                    p className = "dark:text-gray-300" > $ { parseFloat(selectedRequest.total_price).toFixed(2) } < /p> < /
                    div > <
                    /div>

                    { getLevelStatus('woreda', selectedRequest) } { getLevelStatus('zone', selectedRequest) } { getLevelStatus('region', selectedRequest) }

                    {
                        canZoneAdminAct(selectedRequest) && requestStatus && ( <
                            div className = "mt-4" >
                            <
                            label className = "block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2" >
                            Reason
                            for { requestStatus === 'Approved' ? 'approval' : 'rejection' }:
                            <
                            /label> <
                            textarea value = { decisionReason }
                            onChange = {
                                (e) => setDecisionReason(e.target.value)
                            }
                            className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
                            rows = "3"
                            placeholder = { `Enter reason for ${requestStatus === 'Approved' ? 'approval' : 'rejection'}...` }
                            /> <
                            div className = "flex space-x-4 mt-4" >
                            <
                            button onClick = { confirmStatusUpdate }
                            className = { `px-4 py-2 rounded-lg ${
                                                    requestStatus === 'Approved' 
                                                        ? 'bg-green-600 hover:bg-green-700 text-white'
                                                        : 'bg-red-600 hover:bg-red-700 text-white'
                                                }` } >
                            Confirm { requestStatus } <
                            /button> <
                            button onClick = {
                                () => {
                                    setShowRequestModal(false);
                                    setRequestStatus('');
                                    setDecisionReason('');
                                }
                            }
                            className = "bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg" >
                            Cancel <
                            /button> < /
                            div > <
                            /div>
                        )
                    }

                    {
                        !requestStatus && ( <
                            button onClick = {
                                () => setShowRequestModal(false)
                            }
                            className = "bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg mt-4" >
                            Close <
                            /button>
                        )
                    } <
                    /div>
                )
            } <
            /div> < /
            div >
        )
    } <
    /div>
);

const renderFarmers = () => ( <
    div className = "space-y-6" >
    <
    h2 className = "text-2xl font-bold text-gray-800 dark:text-white" > Farmers in Your Zone < /h2>

    <
    div className = "bg-white dark:bg-gray-800 rounded-lg shadow overflow-hidden" >
    <
    table className = "w-full" >
    <
    thead className = "bg-gray-50 dark:bg-gray-700" >
    <
    tr >
    <
    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Full Name <
    /th> <
    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Phone Number <
    /th> <
    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Region <
    /th> <
    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Zone <
    /th> <
    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Woreda <
    /th> < /
    tr > <
    /thead> <
    tbody className = "divide-y divide-gray-200 dark:divide-gray-600" > {
        farmers.map((farmer) => ( <
            tr key = { farmer.id }
            className = "hover:bg-gray-50 dark:hover:bg-gray-700" >
            <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { farmer.full_name } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { farmer.phone_number } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { farmer.region_name } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { farmer.zone_name } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { farmer.woreda_name } <
            /td> < /
            tr >
        ))
    } {
        farmers.length === 0 && ( <
            tr >
            <
            td colSpan = "5"
            className = "px-6 py-4 text-center text-gray-500 dark:text-gray-400" >
            No farmers found in your zone <
            /td> < /
            tr >
        )
    } <
    /tbody> < /
    table > <
    /div> < /
    div >
);

const renderReports = () => ( <
    div className = "space-y-6" >
    <
    div className = "flex justify-between items-center" >
    <
    h2 className = "text-2xl font-bold text-gray-800 dark:text-white" > Reports < /h2> <
    button onClick = {
        () => setShowReportModal(true)
    }
    className = "bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg" >
    Submit Report <
    /button> < /
    div >

    {
        showReportModal && ( <
            div className = "fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" >
            <
            div className = "bg-white dark:bg-gray-800 p-6 rounded-lg w-full max-w-2xl max-h-[80vh] overflow-y-auto" >
            <
            h3 className = "text-xl font-semibold mb-4 dark:text-white" > Submit New Report < /h3> <
            form onSubmit = { handleReportSubmit }
            className = "space-y-4" >
            <
            div >
            <
            label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
            Report Title <
            /label> <
            input type = "text"
            required value = { reportForm.title }
            onChange = {
                (e) => setReportForm({...reportForm, title: e.target.value })
            }
            className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
            placeholder = "Enter report title" /
            >
            <
            /div>

            <
            div >
            <
            label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
            Report Type <
            /label> <
            select value = { reportForm.reportType }
            onChange = {
                (e) => setReportForm({...reportForm, reportType: e.target.value })
            }
            className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white" >
            <
            option value = "Misconduct" > Misconduct < /option> <
            option value = "Corruption" > Corruption < /option> <
            option value = "Incompetence" > Incompetence < /option> <
            option value = "Other" > Other < /option> < /
            select > <
            /div>

            <
            div >
            <
            label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
            Reported Admin <
            /label> <
            select required value = { reportForm.reportedAdminId }
            onChange = {
                (e) => setReportForm({...reportForm, reportedAdminId: e.target.value })
            }
            className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white" >
            <
            option value = "" > Select Admin < /option> {
            woredaAdmins.map((admin) => ( <
                option key = { admin.id }
                value = { admin.id } > { admin.full_name }({ admin.woreda_name }) <
                /option>
            ))
        } <
        /select> < /
        div >

        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
        Priority Level <
        /label> <
        select value = { reportForm.priority }
        onChange = {
            (e) => setReportForm({...reportForm, priority: e.target.value })
        }
        className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white" >
        <
        option value = "Low" > Low < /option> <
        option value = "Medium" > Medium < /option> <
        option value = "High" > High < /option> <
        option value = "Critical" > Critical < /option> < /
        select > <
        /div>

        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
        Description <
        /label> <
        textarea required value = { reportForm.description }
        onChange = {
            (e) => setReportForm({...reportForm, description: e.target.value })
        }
        className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
        rows = "4"
        placeholder = "Provide detailed description of the issue..." /
        >
        <
        /div>

        <
        div >
        <
        label className = "block text-sm font-medium text-gray-700 dark:text-gray-300" >
        Evidence(Optional) <
        /label> <
        textarea value = { reportForm.evidence }
        onChange = {
            (e) => setReportForm({...reportForm, evidence: e.target.value })
        }
        className = "w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
        rows = "2"
        placeholder = "Provide any evidence or supporting information..." /
        >
        <
        /div>

        <
        div className = "flex space-x-4" >
        <
        button type = "submit"
        className = "bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg" >
        Submit Report <
        /button> <
        button type = "button"
        onClick = {
            () => setShowReportModal(false)
        }
        className = "bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg" >
        Cancel <
        /button> < /
        div > <
        /form> < /
        div > <
        /div>
    )
}

<
div className = "bg-white dark:bg-gray-800 rounded-lg shadow overflow-hidden" >
    <
    table className = "w-full" >
    <
    thead className = "bg-gray-50 dark:bg-gray-700" >
    <
    tr >
    <
    th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Title <
    /th> <
th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Type <
    /th> <
th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Reported Admin <
    /th> <
th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Priority <
    /th> <
th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Status <
    /th> <
th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider" >
    Actions <
    /th> < /
    tr > <
    /thead> <
tbody className = "divide-y divide-gray-200 dark:divide-gray-600" > {
        reports.map((report) => ( <
            tr key = { report.id }
            className = "hover:bg-gray-50 dark:hover:bg-gray-700" >
            <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { report.title } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { report.report_type } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white" > { report.reported_admin_name } <
            /td> <
            td className = "px-6 py-4 whitespace-nowrap text-sm" >
            <
            span className = { `px-2 py-1 rounded-full text-xs font-medium ${
                                        report.priority === 'High' ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300' :
                                        report.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300' :
                                        report.priority === 'Low' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300' :
                                        'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300'
                                    }` } > { report.priority } <
            /span> < /
            td > <
            td className = "px-6 py-4 whitespace-nowrap text-sm" >
            <
            span className = { `px-2 py-1 rounded-full text-xs font-medium ${
                                        report.status === 'Pending' ? 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300' :
                                        report.status === 'Under Review' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300' :
                                        report.status === 'Resolved' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300' :
                                        'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300'
                                    }` } > { report.status } <
            /span> < /
            td > <
            td className = "px-6 py-4 whitespace-nowrap text-sm" >
            <
            button onClick = {
                () => deleteReport(report.id)
            }
            className = "text-red-600 hover:text-red-900 dark:text-red-400 dark:hover:text-red-300" >
            Delete <
            /button> < /
            td > <
            /tr>
        ))
    } {
        reports.length === 0 && ( <
            tr >
            <
            td colSpan = "6"
            className = "px-6 py-4 text-center text-gray-500 dark:text-gray-400" >
            No reports found <
            /td> < /
            tr >
        )
    } <
    /tbody> < /
    table > <
    /div> < /
    div >
);

return ( <
    div className = "min-h-screen bg-gray-100 dark:bg-gray-900" > { /* Header */ } <
    header className = "bg-white dark:bg-gray-800 shadow-sm" >
    <
    div className = "max-w-7xl mx-auto px-4 sm:px-6 lg:px-8" >
    <
    div className = "flex justify-between items-center py-4" >
    <
    h1 className = "text-2xl font-bold text-gray-900 dark:text-white" > Zone Dashboard < /h1> <
    div className = "flex items-center space-x-4" >
    <
    span className = "text-gray-700 dark:text-gray-300" >
    Welcome, <
    /span> <
    button onClick = {
        () => setDarkMode(!darkMode)
    }
    className = "p-2 rounded-lg bg-gray-200 dark:bg-gray-700" > { darkMode ? '🌞' : '🌙' } <
    /button> <
    button onClick = { handleLogout }
    className = "bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg" >
    Logout <
    /button> < /
    div > <
    /div> < /
    div > <
    /header>

    { /* Navigation Tabs */ } <
    div className = "max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6" >
    <
    div className = "border-b border-gray-200 dark:border-gray-700" >
    <
    nav className = "-mb-px flex space-x-8" > {
        [
            { id: 'overview', label: 'Overview' },
            { id: 'woreda-admins', label: 'Woreda Admins' },
            { id: 'products', label: 'Products' },
            { id: 'requests', label: 'Requests' },
            { id: 'farmers', label: 'Farmers' },
            { id: 'reports', label: 'Reports' }
        ].map((tab) => ( <
            button key = { tab.id }
            onClick = {
                () => setActiveTab(tab.id)
            }
            className = { `py-4 px-1 border-b-2 font-medium text-sm ${
                                    activeTab === tab.id
                                        ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300'
                                }` } > { tab.label } <
            /button>
        ))
    } <
    /nav> < /
    div >

    { /* Tab Content */ } <
    div className = "py-6" > { activeTab === 'overview' && renderOverview() } { activeTab === 'woreda-admins' && renderWoredaAdmins() } { activeTab === 'products' && renderProducts() } { activeTab === 'requests' && renderRequests() } { activeTab === 'farmers' && renderFarmers() } { activeTab === 'reports' && renderReports() } <
    /div> < /
    div > <
    /div>
);
};

export default ZoneDashboard;
