import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged, signInAnonymously } from 'firebase/auth';
import { getFirestore, collection, onSnapshot, serverTimestamp, addDoc, updateDoc, doc, setDoc, query, where } from 'firebase/firestore';
import { useForm } from 'react-hook-form';

// The following global variables are provided by the Canvas environment.
// We must always use them for Firebase setup.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';

// ----------------------
// Firebase Initialization
// ----------------------
// Initialize Firebase app and services
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// ----------------------
// Mock User Data for demonstration
// In a real application, these users would be managed in Firestore or another auth service
// ----------------------
const MOCK_USERS = {
    '111': { uid: 'fed_admin_uid_1', role: 'federal_admin', phone: '111', password: '123' },
    '222': { uid: 'reg_admin_uid_2', role: 'region_admin', phone: '222', password: '123', region: 'Oromia' },
    '333': { uid: 'zone_admin_uid_3', role: 'zone_admin', phone: '333', password: '123', region: 'Oromia', zone: 'East Shewa' },
    '444': { uid: 'keb_rep_uid_4', role: 'kebele_rep', phone: '444', password: '123', region: 'Oromia', zone: 'East Shewa', kebele: 'Dukem' },
    '555': { uid: 'farmer_uid_5', role: 'farmer', phone: '555', password: '123', region: 'Oromia', zone: 'East Shewa', kebele: 'Dukem' },
};

// ----------------------
// Main App Component
// ----------------------
export default function App() {
    const [currentUser, setCurrentUser] = useState(null);
    const [authReady, setAuthReady] = useState(false);
    const [requests, setRequests] = useState([]);
    const [products, setProducts] = useState([]);
    const [modalContent, setModalContent] = useState('');
    const [showModal, setShowModal] = useState(false);
    const [currentPage, setCurrentPage] = useState('dashboard');
    const [selectedRequest, setSelectedRequest] = useState(null);

    // Get user details without optional chaining
    const userId = currentUser ? currentUser.uid : null;
    const userRole = currentUser ? currentUser.role : null;
    const userRegion = currentUser ? currentUser.region : null;
    const userZone = currentUser ? currentUser.zone : null;
    const userKebele = currentUser ? currentUser.kebele : null;

    // ----------------------
    // Modal Component
    // ----------------------
    const Modal = ({ show, onClose, title, message }) => {
        if (!show) return null;
        return ( <
            div className = "fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center z-50" >
            <
            div className = "bg-white p-6 rounded-lg shadow-xl max-w-sm w-full" >
            <
            h3 className = "text-xl font-semibold text-gray-900 mb-4" > { title } < /h3> <
            p className = "text-gray-700 mb-6" > { message } < /p> <
            div className = "flex justify-end" >
            <
            button onClick = { onClose }
            className = "px-4 py-2 bg-indigo-600 text-white font-medium text-sm rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2" >
            Close <
            /button> <
            /div> <
            /div> <
            /div>
        );
    };

    const notify = (message) => {
        setModalContent(message);
        setShowModal(true);
    };

    // ----------------------
    // Auth Effect
    // ----------------------
    useEffect(() => {
        const checkAuth = async() => {
            try {
                if (initialAuthToken) {
                    await signInWithCustomToken(auth, initialAuthToken);
                } else {
                    await signInAnonymously(auth);
                }
            } catch (error) {
                console.error("Firebase Auth Error:", error);
                notify("Failed to authenticate. Please check the console for details.");
            }
        };

        const unsubscribe = onAuthStateChanged(auth, async(user) => {
            if (user) {
                // For a self-contained demo, we'll use a hardcoded user lookup
                const mockUser = MOCK_USERS['111']; // Default to federal admin for testing
                if (mockUser) {
                    setCurrentUser(mockUser);
                } else {
                    setCurrentUser({ uid: user.uid, role: 'unknown' });
                }
            } else {
                setCurrentUser(null);
            }
            setAuthReady(true);
        });

        if (!auth.currentUser) {
            checkAuth();
        }

        return () => unsubscribe();
    }, []);

    // ----------------------
    // Data Subscription Effect
    // ----------------------
    useEffect(() => {
        if (!authReady || !currentUser) return;

        const allRequestsPath = `artifacts/${appId}/public/data/all-requests`;
        let requestsQuery;

        switch (userRole) {
            case 'federal_admin':
                requestsQuery = collection(db, allRequestsPath);
                break;
            case 'region_admin':
                requestsQuery = query(collection(db, allRequestsPath), where('region', '==', userRegion));
                break;
            case 'zone_admin':
                requestsQuery = query(collection(db, allRequestsPath), where('zone', '==', userZone));
                break;
            case 'kebele_rep':
                requestsQuery = query(collection(db, allRequestsPath), where('kebele', '==', userKebele));
                break;
            case 'farmer':
                requestsQuery = query(collection(db, allRequestsPath), where('requesterId', '==', userId));
                break;
            default:
                setRequests([]);
                return;
        }

        const unsubscribeRequests = onSnapshot(requestsQuery, (snapshot) => {
            const fetchedRequests = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            // Sort by timestamp for consistent order
            const sortedRequests = fetchedRequests.sort((a, b) => {
                const aTime = a.timestamp ? a.timestamp.seconds : 0;
                const bTime = b.timestamp ? b.timestamp.seconds : 0;
                return bTime - aTime;
            });
            setRequests(sortedRequests);
        }, (error) => {
            console.error("Error fetching requests:", error);
            notify("Failed to fetch requests.");
        });

        const unsubscribeProducts = onSnapshot(collection(db, `artifacts/${appId}/public/data/products`), (snapshot) => {
            const fetchedProducts = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setProducts(fetchedProducts);
        }, (error) => {
            console.error("Error fetching products:", error);
            notify("Failed to fetch products.");
        });


        return () => {
            unsubscribeRequests();
            unsubscribeProducts();
        };
    }, [authReady, currentUser, userId, userRole, userRegion, userZone, userKebele]);

    // ----------------------
    // Login Component
    // ----------------------
    const LoginPage = () => {
            const { register, handleSubmit, formState: { errors } } = useForm();

            const onSubmit = async(data) => {
                const user = Object.values(MOCK_USERS).find(u => u.phone === data.phone && u.password === data.password);
                if (user) {
                    // Simulate setting the user in state, bypassing Firebase auth for demo
                    setCurrentUser(user);
                    notify(`Welcome back, ${user.role.toUpperCase()}!`);
                    setCurrentPage('dashboard');
                } else {
                    notify('Invalid phone number or password. Please try again.');
                }
            };

            return ( <
                div className = "flex items-center justify-center min-h-screen bg-gray-900" >
                <
                div className = "w-full max-w-md p-8 space-y-8 bg-gray-800 rounded-xl shadow-lg text-white" >
                <
                div className = "text-center" >
                <
                span className = "text-4xl" > 🔐 < /span> <
                h2 className = "mt-6 text-3xl font-extrabold text-white" >
                Sign in to your account <
                /h2> <
                p className = "mt-2 text-sm text-gray-400" >
                Use the mock credentials to test. <
                /p> <
                /div> <
                form className = "mt-8 space-y-6"
                onSubmit = { handleSubmit(onSubmit) } >
                <
                div >
                <
                label htmlFor = "phone"
                className = "sr-only" > Phone Number < /label> <
                input id = "phone"
                type = "text" {...register('phone', { required: 'Phone is required' }) }
                className = "appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-700 bg-gray-700 placeholder-gray-400 text-white focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                placeholder = "Phone Number" /
                > {
                    errors.phone && < p className = "mt-2 text-sm text-red-500" > { errors.phone.message } < /p>} <
                    /div> <
                    div >
                    <
                    label htmlFor = "password"
                    className = "sr-only" > Password < /label> <
                    input
                    id = "password"
                    type = "password" {...register('password', { required: 'Password is required' }) }
                    className = "appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-700 bg-gray-700 placeholder-gray-400 text-white focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    placeholder = "Password" /
                    > {
                        errors.password && < p className = "mt-2 text-sm text-red-500" > { errors.password.message } < /p>} <
                        /div> <
                        div >
                        <
                        button
                        type = "submit"
                        className = "group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" >
                        <
                        span className = "absolute left-0 inset-y-0 flex items-center pl-3" >
                        <
                        span className = "h-5 w-5 text-indigo-500 group-hover:text-indigo-400" > 👤 < /span> <
                        /span>
                        Sign in
                        <
                        /button> <
                        /div> <
                        /form> <
                        /div> <
                        /div>
                    );
                };

                // ----------------------
                // Admin Management Component
                // ----------------------
                const AdminManagement = ({ title, userLevel }) => {
                    const { register, handleSubmit, reset } = useForm();
                    const onSubmit = async(data) => {
                        // This would be a real user creation flow. For this demo, we just simulate it.
                        notify(`Simulating creation of new ${userLevel} with phone: ${data.phone}.`);
                        reset();
                    };

                    return ( <
                        div className = "max-w-2xl mx-auto p-4 bg-white shadow-md rounded-lg mt-8" >
                        <
                        h3 className = "text-xl font-bold mb-4" > { title } < /h3> <
                        form onSubmit = { handleSubmit(onSubmit) }
                        className = "space-y-4" >
                        <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Phone Number < /label> <
                        input {...register('phone', { required: true }) }
                        className = "mt-1 block w-full p-2 border rounded-md" / >
                        <
                        /div> <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Password < /label> <
                        input type = "password" {...register('password', { required: true }) }
                        className = "mt-1 block w-full p-2 border rounded-md" / >
                        <
                        /div> <
                        button type = "submit"
                        className = "px-4 py-2 bg-blue-600 text-white rounded-md" > Create User < /button> <
                        /form> <
                        /div>
                    );
                };

                // ----------------------
                // Product Management Component
                // ----------------------
                const ProductManagement = () => {
                    const { register, handleSubmit, reset } = useForm();
                    const onSubmit = async(data) => {
                        try {
                            await addDoc(collection(db, `artifacts/${appId}/public/data/products`), data);
                            notify('Product added successfully!');
                            reset();
                        } catch (e) {
                            console.error("Error adding product:", e);
                            notify('Failed to add product.');
                        }
                    };

                    const deleteProduct = async(productId) => {
                        try {
                            await setDoc(doc(db, `artifacts/${appId}/public/data/products`, productId), { isDeleted: true }, { merge: true });
                            notify('Product deleted successfully!');
                        } catch (e) {
                            console.error("Error deleting product:", e);
                            notify('Failed to delete product.');
                        }
                    };

                    return ( <
                        div className = "max-w-4xl mx-auto p-4 bg-white shadow-md rounded-lg mt-8" >
                        <
                        h3 className = "text-xl font-bold mb-4" > Manage Products < /h3> <
                        form onSubmit = { handleSubmit(onSubmit) }
                        className = "space-y-4 mb-8" >
                        <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Product Name < /label> <
                        input {...register('name', { required: true }) }
                        className = "mt-1 block w-full p-2 border rounded-md" / >
                        <
                        /div> <
                        button type = "submit"
                        className = "px-4 py-2 bg-blue-600 text-white rounded-md" > Add Product < /button> <
                        /form> <
                        div className = "space-y-4" > {
                            products.filter(p => !p.isDeleted).map(product => ( <
                                div key = { product.id }
                                className = "flex justify-between items-center bg-gray-100 p-3 rounded-md" >
                                <
                                span > { product.name } < /span> <
                                button onClick = {
                                    () => deleteProduct(product.id) }
                                className = "text-red-500 hover:text-red-700" > Delete < /button> <
                                /div>
                            ))
                        } <
                        /div> <
                        /div>
                    );
                };

                // ----------------------
                // New Request Form
                // ----------------------
                const NewRequestForm = () => {
                    const { register, handleSubmit, reset } = useForm();
                    const onSubmit = async(data) => {
                        const requestData = {
                            ...data,
                            status: 'pending',
                            requesterId: userId,
                            timestamp: serverTimestamp(),
                            region: userRegion,
                            zone: userZone,
                            kebele: userKebele
                        };
                        try {
                            const docRef = await addDoc(collection(db, `artifacts/${appId}/public/data/all-requests`), requestData);
                            await setDoc(doc(db, `artifacts/${appId}/users/${userId}/requests`, docRef.id), requestData);
                            notify('Request submitted successfully!');
                            reset();
                        } catch (e) {
                            console.error("Error adding document: ", e);
                            notify('Failed to submit request.');
                        }
                    };

                    return ( <
                        div className = "max-w-2xl mx-auto p-4 bg-white shadow-md rounded-lg mt-8" >
                        <
                        h2 className = "text-2xl font-bold text-center text-gray-800 mb-6" > New Product Request < /h2> <
                        form onSubmit = { handleSubmit(onSubmit) }
                        className = "space-y-6" >
                        <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Product < /label> <
                        select {...register('productName', { required: true }) }
                        className = "mt-1 block w-full p-2 border rounded-md" >
                        <
                        option value = "" > Select a product... < /option> {
                            products.filter(p => !p.isDeleted).map(p => ( <
                                option key = { p.id }
                                value = { p.name } > { p.name } < /option>
                            ))
                        } <
                        /select> <
                        /div> <
                        div >
                        <
                        label className = "block text-sm font-medium text-gray-700" > Quantity < /label> <
                        input type = "number" {...register('quantity', { required: true, valueAsNumber: true, min: 1 }) }
                        className = "mt-1 block w-full p-2 border rounded-md" / >
                        <
                        /div> <
                        button type = "submit"
                        className = "px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700" > Submit Request < /button> <
                        /form> <
                        /div>
                    );
                };

                // ----------------------
                // Request Approval Component
                // ----------------------
                const RequestApproval = ({ request }) => {
                    const handleStatusChange = async(status) => {
                        try {
                            const docRef = doc(db, `artifacts/${appId}/public/data/all-requests`, request.id);
                            await updateDoc(docRef, { status, approvedBy: userId, approvalTimestamp: serverTimestamp() });
                            notify(`Request ${status} successfully.`);
                            setSelectedRequest(null);
                        } catch (e) {
                            console.error("Error updating request status:", e);
                            notify('Failed to update request status.');
                        }
                    };

                    return ( <
                        div className = "max-w-2xl mx-auto p-4 bg-white shadow-md rounded-lg mt-8" >
                        <
                        h3 className = "text-xl font-bold mb-4" > Request Details < /h3> <
                        div className = "space-y-2" >
                        <
                        p > < strong > Item Name: < /strong> {request.productName}</p >
                        <
                        p > < strong > Quantity: < /strong> {request.quantity}</p >
                        <
                        p > < strong > Status: < /strong> {request.status}</p >
                        <
                        /div> {
                            request.status === 'pending' && userRole === 'kebele_rep' && ( <
                                div className = "mt-4 flex space-x-4" >
                                <
                                button onClick = {
                                    () => handleStatusChange('approved') }
                                className = "px-4 py-2 bg-green-500 text-white rounded-md" > Approve < /button> <
                                button onClick = {
                                    () => handleStatusChange('rejected') }
                                className = "px-4 py-2 bg-red-500 text-white rounded-md" > Reject < /button> <
                                /div>
                            )
                        } <
                        button onClick = {
                            () => setSelectedRequest(null) }
                        className = "mt-4 px-4 py-2 bg-gray-500 text-white rounded-md" > Back < /button> <
                        /div>
                    );
                };

                // ----------------------
                // Dashboard Component
                // ----------------------
                const Dashboard = () => {
                    return ( <
                        div className = "p-8" >
                        <
                        h2 className = "text-3xl font-extrabold text-gray-900 mb-6 text-center" > Dashboard < /h2> <
                        div className = "bg-white rounded-lg shadow-md p-6" >
                        <
                        h3 className = "text-xl font-bold mb-4" > Requests < /h3> <
                        div className = "overflow-x-auto" >
                        <
                        table className = "min-w-full divide-y divide-gray-200" >
                        <
                        thead className = "bg-gray-50" >
                        <
                        tr >
                        <
                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Product Name < /th> <
                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Quantity < /th> <
                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Location < /th> <
                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Status < /th> <
                        th className = "px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" > Actions < /th> <
                        /tr> <
                        /thead> <
                        tbody className = "bg-white divide-y divide-gray-200" > {
                            requests.length > 0 ? (
                                requests.map((request) => ( <
                                    tr key = { request.id } >
                                    <
                                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.productName } < /td> <
                                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.quantity } < /td> <
                                    td className = "px-6 py-4 whitespace-nowrap text-sm text-gray-500" > { request.kebele } < /td> <
                                    td className = "px-6 py-4 whitespace-nowrap text-sm" >
                                    <
                                    span className = { `px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${request.status === 'pending' ? 'bg-yellow-100 text-yellow-800' : request.status === 'approved' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}` } > { request.status } <
                                    /span> <
                                    /td> <
                                    td className = "px-6 py-4 whitespace-nowrap text-sm font-medium" > {
                                        userRole === 'kebele_rep' && request.status === 'pending' && ( <
                                            button onClick = {
                                                () => setSelectedRequest(request) }
                                            className = "text-indigo-600 hover:text-indigo-900" > Approve / Reject < /button>
                                        )
                                    } <
                                    /td> <
                                    /tr>
                                ))
                            ) : ( <
                                tr >
                                <
                                td colSpan = "5"
                                className = "px-6 py-4 text-center text-sm text-gray-500" > No requests found. < /td> <
                                /tr>
                            )
                        } <
                        /tbody> <
                        /table> <
                        /div> <
                        /div> <
                        /div>
                    );
                };

                // ----------------------
                // Main App Render Logic
                // ----------------------
                const renderContent = () => {
                    if (!currentUser) {
                        return <LoginPage / > ;
                    }

                    if (selectedRequest) {
                        return <RequestApproval request = { selectedRequest }
                        />;
                    }

                    switch (currentPage) {
                        case 'new-request':
                            return <NewRequestForm / > ;
                        case 'product-management':
                            if (userRole === 'federal_admin' || userRole === 'region_admin' || userRole === 'zone_admin') {
                                return <ProductManagement / > ;
                            }
                            return <Dashboard / > ;
                        case 'admin-management':
                            if (userRole === 'federal_admin') return <AdminManagement title = "Create Region Admin"
                            userLevel = "region_admin" / > ;
                            if (userRole === 'region_admin') return <AdminManagement title = "Create Zone Admin"
                            userLevel = "zone_admin" / > ;
                            if (userRole === 'zone_admin') return <AdminManagement title = "Create Kebele Rep"
                            userLevel = "kebele_rep" / > ;
                            if (userRole === 'kebele_rep') return <AdminManagement title = "Create Farmer"
                            userLevel = "farmer" / > ;
                            return <Dashboard / > ;
                        case 'dashboard':
                        default:
                            return <Dashboard / > ;
                    }
                };

                return ( <
                    div className = "bg-gray-100 min-h-screen font-sans antialiased text-gray-800" > {
                        currentUser && ( <
                            header className = "bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-10" >
                            <
                            h1 className = "text-xl font-bold text-blue-600" > FARMERS Platform < /h1> <
                            nav className = "flex items-center space-x-4" >
                            <
                            button onClick = {
                                () => setCurrentPage('dashboard') }
                            className = "text-gray-600 hover:text-blue-600 focus:outline-none" > Dashboard < /button> {
                                userRole === 'farmer' && ( <
                                    button onClick = {
                                        () => setCurrentPage('new-request') }
                                    className = "text-gray-600 hover:text-blue-600 focus:outline-none" > New Request < /button>
                                )
                            } {
                                ['federal_admin', 'region_admin', 'zone_admin'].includes(userRole) && ( <
                                    button onClick = {
                                        () => setCurrentPage('product-management') }
                                    className = "text-gray-600 hover:text-blue-600 focus:outline-none" > Manage Products < /button>
                                )
                            } {
                                ['federal_admin', 'region_admin', 'zone_admin', 'kebele_rep'].includes(userRole) && ( <
                                    button onClick = {
                                        () => setCurrentPage('admin-management') }
                                    className = "text-gray-600 hover:text-blue-600 focus:outline-none" > Manage Users < /button>
                                )
                            } <
                            span className = "text-sm text-gray-500" > Role: { userRole } < /span> <
                            button onClick = {
                                () => setCurrentUser(null) }
                            className = "bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md transition duration-150" > Sign Out < /button> <
                            /nav> <
                            /header>
                        )
                    }

                    <
                    main className = "container mx-auto p-4" > { renderContent() } <
                    /main>

                    <
                    Modal show = { showModal }
                    onClose = {
                        () => setShowModal(false) }
                    title = "Notification"
                    message = { modalContent }
                    /> <
                    /div>
                );
            }
